      });
    }

    try {
      const { parseExcelFile } = await import('./excelUtils');
      const fileBuffer = await fs.promises.readFile(req.file.path);
      const parsedData = parseExcelFile(fileBuffer);

      // Import data in order: foundational data first, then dependent data
      let importCounts = {
        categories: 0,
        products: 0,
        users: 0,
        orders: 0,
        orderItems: 0,
        unitsOfMeasure: 0,
        sliderImages: 0,
        siteSettings: 0
      };

      if (parsedData.categories.length > 0) {
        await storage.importCategories(parsedData.categories);
        importCounts.categories = parsedData.categories.length;
      }

      if (parsedData.unitsOfMeasure.length > 0) {
        await storage.importUnitsOfMeasure(parsedData.unitsOfMeasure);
        importCounts.unitsOfMeasure = parsedData.unitsOfMeasure.length;
      }

      if (parsedData.products.length > 0) {
        await storage.importProducts(parsedData.products);
        importCounts.products = parsedData.products.length;
      }

      if (parsedData.users.length > 0) {
        await storage.importUsers(parsedData.users);
        importCounts.users = parsedData.users.length;
      }

      if (parsedData.orders.length > 0) {
        await storage.importOrders(parsedData.orders);
        importCounts.orders = parsedData.orders.length;
      }

      if (parsedData.orderItems.length > 0) {
        await storage.importOrderItems(parsedData.orderItems);
        importCounts.orderItems = parsedData.orderItems.length;
      }

      if (parsedData.sliderImages.length > 0) {
        await storage.importSliderImages(parsedData.sliderImages);
        importCounts.sliderImages = parsedData.sliderImages.length;
      }

      if (parsedData.siteSettings.length > 0) {
        await storage.importSiteSettings(parsedData.siteSettings);
        importCounts.siteSettings = parsedData.siteSettings.length;
      }

      // Clean up uploaded file
      await fs.promises.unlink(req.file.path);

      const totalImported = Object.values(importCounts).reduce((sum, count) => sum + count, 0);

      res.json({ 
        success: true, 
        message: `Successfully imported ${totalImported} total records: ${importCounts.categories} categories, ${importCounts.products} products, ${importCounts.users} users, ${importCounts.orders} orders, ${importCounts.orderItems} order items, ${importCounts.unitsOfMeasure} units of measure, ${importCounts.sliderImages} slider images, and ${importCounts.siteSettings} site settings`,
        imported: importCounts
      });
    } catch (error) {
      console.error('Excel import error:', error);

      // Clean up uploaded file on error
      if (req.file) {
        try {
          await fs.promises.unlink(req.file.path);
        } catch (cleanupError) {
          console.error('Failed to cleanup upload file:', cleanupError);
        }
      }

      res.status(500).json({ 
        success: false, 
        message: error instanceof Error ? error.message : "Failed to import Excel file" 
      });
    }
  });

  // Individual sheet import routes  
  app.post("/api/admin/import/excel/:sheetType", excelUpload.single('excel'), async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isAdmin) {
      return res.sendStatus(401);
    }

    if (!req.file) {
      return res.status(400).json({ 
        success: false, 
        message: "No Excel file uploaded" 
      });
    }

    const { sheetType } = req.params;

    try {
      const { parseExcelFile } = await import('./excelUtils');
      const fileBuffer = await fs.promises.readFile(req.file.path);
      const parsedData = parseExcelFile(fileBuffer);

      let importCount = 0;
      let successMessage = "";

      switch (sheetType) {
        case 'products':
          if (parsedData.products.length > 0) {
            await storage.importProducts(parsedData.products);
            importCount = parsedData.products.length;
            successMessage = `Successfully imported ${importCount} products`;
          }
          break;
        case 'categories':
          if (parsedData.categories.length > 0) {
            await storage.importCategories(parsedData.categories);
            importCount = parsedData.categories.length;
            successMessage = `Successfully imported ${importCount} categories`;
          }
          break;
        case 'users':
          if (parsedData.users.length > 0) {
            await storage.importUsers(parsedData.users);
            importCount = parsedData.users.length;
            successMessage = `Successfully imported ${importCount} users`;
          }
          break;
        case 'orders':
          if (parsedData.orders.length > 0) {
            await storage.importOrders(parsedData.orders);
            importCount = parsedData.orders.length;
            successMessage = `Successfully imported ${importCount} orders`;
          }
          break;
        case 'order-items':
          if (parsedData.orderItems.length > 0) {
            await storage.importOrderItems(parsedData.orderItems);
            importCount = parsedData.orderItems.length;
            successMessage = `Successfully imported ${importCount} order items`;
          }
          break;
        case 'units':
          if (parsedData.unitsOfMeasure.length > 0) {
            await storage.importUnitsOfMeasure(parsedData.unitsOfMeasure);
            importCount = parsedData.unitsOfMeasure.length;
            successMessage = `Successfully imported ${importCount} units of measure`;
          }
          break;
        case 'site-settings':
          if (parsedData.siteSettings.length > 0) {
            await storage.importSiteSettings(parsedData.siteSettings);
            importCount = parsedData.siteSettings.length;
            successMessage = `Successfully imported ${importCount} site settings`;
          }
          break;
        case 'slider-images':
          if (parsedData.sliderImages.length > 0) {
            await storage.importSliderImages(parsedData.sliderImages);
            importCount = parsedData.sliderImages.length;
            successMessage = `Successfully imported ${importCount} slider images`;
          }
          break;
        default:
          await fs.promises.unlink(req.file.path);
          return res.status(400).json({ 
            success: false, 
            message: "Invalid sheet type" 
          });
      }

      // Clean up uploaded file
      await fs.promises.unlink(req.file.path);

      res.json({ 
        success: true, 
        message: successMessage,
        imported: importCount
      });
    } catch (error) {
      console.error(`Excel import error (${sheetType}):`, error);

      // Clean up uploaded file on error
      if (req.file) {
        try {
          await fs.promises.unlink(req.file.path);
        } catch (cleanupError) {
          console.error('Failed to cleanup upload file:', cleanupError);
        }
      }

      res.status(500).json({ 
        success: false, 
        message: error instanceof Error ? error.message : `Failed to import ${sheetType} Excel file` 
      });
    }
  });

  // Permission Management Routes (Super Admin only)
  app.get("/api/admin/roles", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const roles = await storage.getAllRoles();
      res.json(roles);
    } catch (error) {
      console.error("Error fetching roles:", error);
      res.status(500).json({ message: "Failed to fetch roles" });
    }
  });

  app.get("/api/admin/permission-modules", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const modules = await storage.getAllPermissionModules();
      res.json(modules);
    } catch (error) {
      console.error("Error fetching permission modules:", error);
      res.status(500).json({ message: "Failed to fetch permission modules" });
    }
  });

  app.get("/api/admin/permissions/:moduleId", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const { moduleId } = req.params;
      const permissions = await storage.getPermissionsByModule(moduleId);
      res.json(permissions);
    } catch (error) {
      console.error("Error fetching permissions:", error);
      res.status(500).json({ message: "Failed to fetch permissions" });
    }
  });

  app.get("/api/admin/role-permissions/:roleId", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const { roleId } = req.params;
      const permissions = await storage.getRolePermissions(roleId);
      res.json(permissions);
    } catch (error) {
      console.error("Error fetching role permissions:", error);
      res.status(500).json({ message: "Failed to fetch role permissions" });
    }
  });

  app.post("/api/admin/assign-permissions", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const { roleId, permissionIds } = req.body;
      await storage.assignPermissionsToRole(roleId, permissionIds);
      res.json({ message: "Permissions assigned successfully" });
    } catch (error) {
      console.error("Error assigning permissions:", error);
      res.status(500).json({ message: "Failed to assign permissions" });
    }
  });

  app.post("/api/admin/assign-role", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const { userId, roleId } = req.body;
      await storage.assignRoleToUser(userId, roleId);
      res.json({ message: "Role assigned successfully" });
    } catch (error) {
      console.error("Error assigning role:", error);
      res.status(500).json({ message: "Failed to assign role" });
    }
  });

  // Super Admin permission assignment endpoint
  app.post("/api/admin/assign-permissions", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const { roleId, permissionIds } = req.body;
      await storage.assignPermissionsToRole(roleId, permissionIds);
      res.json({ message: "Permissions assigned successfully" });
    } catch (error) {
      console.error("Error assigning permissions:", error);
      res.status(500).json({ message: "Failed to assign permissions" });
    }
  });

  app.get("/api/user/permissions", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const { getUserPermissions } = await import("./seed-permissions");
      const permissions = await getUserPermissions(req.user.id);
      res.json({ permissions });
    } catch (error) {
      console.error("Error fetching user permissions:", error);
      res.status(500).json({ message: "Failed to fetch user permissions" });
    }
  });

  // Update user profile
  app.patch("/api/user/profile", requirePermission("users.profile"), async (req, res) => {
    try {
      const userId = req.user!.id;
      const { firstName, lastName, email } = req.body;

      const updatedUser = await storage.updateUserProfile(userId, {
        firstName,
        lastName, 
        email
      });

      res.json(updatedUser);
    } catch (error) {
      console.error("Error updating user profile:", error);
      res.status(500).json({ error: "Failed to update profile" });
    }
  });

  // Role and Permission Management Routes (Super Admin Only)
  app.get("/api/admin/roles", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const roles = await storage.getAllRoles();
      res.json(roles);
    } catch (error: any) {
      console.error("Error fetching roles:", error);
      res.status(500).json({ message: error.message || "Failed to fetch roles" });
    }
  });

  app.get("/api/admin/permission-modules", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const modules = await storage.getAllPermissionModules();
      res.json(modules);
    } catch (error: any) {
      console.error("Error fetching permission modules:", error);
      res.status(500).json({ message: error.message || "Failed to fetch permission modules" });
    }
  });

  app.get("/api/admin/permissions/:moduleId", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const permissions = await storage.getPermissionsByModule(req.params.moduleId);
      res.json(permissions);
    } catch (error: any) {
      console.error("Error fetching permissions:", error);
      res.status(500).json({ message: error.message || "Failed to fetch permissions" });
    }
  });

  app.get("/api/admin/role-permissions/:roleId", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const permissions = await storage.getRolePermissions(req.params.roleId);
      res.json(permissions);
    } catch (error: any) {
      console.error("Error fetching role permissions:", error);
      res.status(500).json({ message: error.message || "Failed to fetch role permissions" });
    }
  });

  app.post("/api/admin/assign-permissions", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const { roleId, permissionIds } = req.body;

      if (!roleId || !Array.isArray(permissionIds)) {
        return res.status(400).json({ message: "Role ID and permission IDs array are required" });
      }

      await storage.assignPermissionsToRole(roleId, permissionIds);
      res.json({ message: "Permissions assigned successfully" });
    } catch (error: any) {
      console.error("Error assigning permissions:", error);
      res.status(500).json({ message: error.message || "Failed to assign permissions" });
    }
  });

  app.post("/api/admin/assign-role", async (req, res) => {
    if (!req.isAuthenticated() || !req.user?.isSuperAdmin) {
      return res.status(401).json({ message: "Super Admin access required" });
    }

    try {
      const { userId, roleId } = req.body;

      if (!userId || !roleId) {
        return res.status(400).json({ message: "User ID and Role ID are required" });
      }

      await storage.assignRoleToUser(userId, roleId);
      res.json({ message: "Role assigned successfully" });
    } catch (error: any) {
      console.error("Error assigning role:", error);
      res.status(500).json({ message: error.message || "Failed to assign role" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/seed-comprehensive-permissions.ts.content:
import { db } from "./db";
import { 
  roles, 
  permissionModules, 
  permissions, 
  rolePermissions, 
  users 
} from "@shared/schema";
import { eq, and } from "drizzle-orm";
import { PERMISSION_MODULES, PERMISSIONS, ROLE_PERMISSIONS } from "./permissions-config";

// Main seeding function for comprehensive permission system
export async function seedComprehensivePermissions() {
  try {
    console.log("üîß Seeding comprehensive permission system...");

    // 1. Clear existing permissions
    console.log("Clearing existing permissions...");
    await db.delete(rolePermissions);
    await db.delete(permissions);
    await db.delete(permissionModules);

    // 2. Create permission modules
    console.log("Creating permission modules...");
    const moduleMap = new Map();

    for (const module of PERMISSION_MODULES) {
      const [createdModule] = await db
        .insert(permissionModules)
        .values({
          name: module.name,
          displayName: module.displayName,
          description: module.description,
          icon: module.icon,
          sortOrder: module.sortOrder
        })
        .returning();

      moduleMap.set(module.name, createdModule.id);
      console.log(`‚úì Created module: ${module.displayName}`);
    }

    // 3. Create permissions
    console.log("Creating permissions...");
    const permissionMap = new Map();

    for (const permission of PERMISSIONS) {
      const moduleId = moduleMap.get(permission.module);
      if (!moduleId) {
        console.warn(`Warning: Module ${permission.module} not found for permission ${permission.name}`);
        continue;
      }

      const [createdPermission] = await db
        .insert(permissions)
        .values({
          moduleId: moduleId,
          name: permission.name,
          displayName: permission.displayName,
          description: permission.displayName, // Use displayName as description for now
          action: permission.action
        })
        .returning();

      permissionMap.set(permission.name, createdPermission.id);
      console.log(`‚úì Created permission: ${permission.displayName}`);
    }

    // 4. Get or create roles
    console.log("Setting up roles...");

    // Super Admin role
    let superAdminRole = await db.select().from(roles).where(eq(roles.name, "super_admin"));
    let superAdminRoleId: string;

    if (superAdminRole.length === 0) {
      [superAdminRole[0]] = await db
        .insert(roles)
        .values({
          name: "super_admin",
          displayName: "Super Administrator",
          description: "Full system access with all permissions",
          isSystemRole: true
        })
        .returning();
    }
    superAdminRoleId = superAdminRole[0].id;

    // Manager role
    let managerRole = await db.select().from(roles).where(eq(roles.name, "manager"));
    let managerRoleId: string;

    if (managerRole.length === 0) {
      [managerRole[0]] = await db
        .insert(roles)
        .values({
          name: "manager",
          displayName: "Manager",
          description: "Limited access - permissions controlled by Super Admin"
        })
        .returning();
    }
    managerRoleId = managerRole[0].id;

    // User role
    let userRole = await db.select().from(roles).where(eq(roles.name, "user"));
    let userRoleId: string;

    if (userRole.length === 0) {
      [userRole[0]] = await db
        .insert(roles)
        .values({
          name: "user",
          displayName: "Customer",
          description: "Basic customer access for shopping and orders"
        })
        .returning();
    }
    userRoleId = userRole[0].id;

    // 5. Assign permissions to roles
    console.log("Assigning permissions to roles...");

    // Super Admin gets all permissions
    const allPermissionIds = Array.from(permissionMap.values());
    for (const permissionId of allPermissionIds) {
      await db
        .insert(rolePermissions)
        .values({
          roleId: superAdminRoleId,
          permissionId: permissionId
        })
        .onConflictDoNothing();
    }
    console.log(`‚úì Assigned ${allPermissionIds.length} permissions to Super Admin`);

    // Manager gets limited permissions
    const managerPermissions = ROLE_PERMISSIONS.manager;
    let assignedManagerCount = 0;
    for (const permissionName of managerPermissions) {
      const permissionId = permissionMap.get(permissionName);
      if (permissionId) {
        await db
          .insert(rolePermissions)
          .values({
            roleId: managerRoleId,
            permissionId: permissionId
          })
          .onConflictDoNothing();
        assignedManagerCount++;
      }
    }
    console.log(`‚úì Assigned ${assignedManagerCount} permissions to Manager role`);

    // User gets basic permissions
    const userPermissions = ROLE_PERMISSIONS.user;
    let assignedUserCount = 0;
    for (const permissionName of userPermissions) {
      const permissionId = permissionMap.get(permissionName);
      if (permissionId) {
        await db
          .insert(rolePermissions)
          .values({
            roleId: userRoleId,
            permissionId: permissionId
          })
          .onConflictDoNothing();
        assignedUserCount++;
      }
    }
    console.log(`‚úì Assigned ${assignedUserCount} permissions to User role`);

    // 6. Update user accounts
    console.log("Updating user accounts...");

    // Update admin user to Super Admin
    const adminUsers = await db
      .select()
      .from(users)
      .where(eq(users.username, "admin"));

    if (adminUsers.length > 0) {
      await db
        .update(users)
        .set({ 
          isSuperAdmin: true,
          isAdmin: true,
          roleId: superAdminRoleId
        })
        .where(eq(users.username, "admin"));

      console.log(`‚úì Updated admin user to Super Admin`);
    }

    // Update manager user to Manager role (with limited permissions)
    const managerUsers = await db
      .select()
      .from(users)
      .where(eq(users.username, "manager"));

    if (managerUsers.length > 0) {
      await db
        .update(users)
        .set({ 
          isSuperAdmin: false, 
          isAdmin: false, // Remove admin flag - use role-based permissions only
          roleId: managerRoleId
        })
        .where(eq(users.username, "manager"));

      console.log(`‚úì Updated manager user to Manager role (limited permissions)`);
    }

    // Update regular user to User role
    const regularUsers = await db
      .select()
      .from(users)
      .where(eq(users.username, "user"));

    if (regularUsers.length > 0) {
      await db
        .update(users)
        .set({ 
          isSuperAdmin: false,
          isAdmin: false,
          roleId: userRoleId
        })
        .where(eq(users.username, "user"));

      console.log(`‚úì Updated user to Customer role`);
    }

    console.log("üéâ Comprehensive permission system seeded successfully!");

    return {
      success: true,
      superAdminRoleId,
      managerRoleId,
      userRoleId,
      modulesCount: PERMISSION_MODULES.length,
      permissionsCount: PERMISSIONS.length,
      totalPermissions: allPermissionIds.length,
      managerPermissions: assignedManagerCount,
      userPermissions: assignedUserCount
    };

  } catch (error) {
    console.error("‚ùå Error seeding comprehensive permissions:", error);
    return { success: false, error };
  }
}

// Helper function to check if user has specific permission
export async function userHasPermission(userId: string, permissionName: string): Promise<boolean> {
  try {
    const [user] = await db
      .select()
      .from(users)
      .where(eq(users.id, userId));

    if (!user) return false;

    // Super admins have all permissions
    if (user.isSuperAdmin) return true;

    // Check if user has the specific permission through their role
    if (!user.roleId) return false;

    const userPermissions = await db
      .select({ permissionName: permissions.name })
      .from(rolePermissions)
      .innerJoin(permissions, eq(rolePermissions.permissionId, permissions.id))
      .where(eq(rolePermissions.roleId, user.roleId));

    return userPermissions.some(p => p.permissionName === permissionName);
  } catch (error) {
    console.error("Error checking user permission:", error);
    return false;
  }
}

// Helper function to get all user permissions
export async function getUserPermissions(userId: string): Promise<string[]> {
  try {
    const [user] = await db
      .select()
      .from(users)
      .where(eq(users.id, userId));

    if (!user) return [];

    // Super admins have all permissions
    if (user.isSuperAdmin) {
      const allPermissions = await db.select({ name: permissions.name }).from(permissions);
      return allPermissions.map(p => p.name);
    }

    // Get permissions through role
    if (!user.roleId) return [];

    const userPermissions = await db
      .select({ permissionName: permissions.name })
      .from(rolePermissions)
      .innerJoin(permissions, eq(rolePermissions.permissionId, permissions.id))
      .where(eq(rolePermissions.roleId, user.roleId));

    return userPermissions.map(p => p.permissionName);
  } catch (error) {
    console.error("Error getting user permissions:", error);
    return [];
  }
}

