import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/use-auth";
import { usePermissions } from "@/hooks/use-permissions";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  DropdownMenu, 
  DropdownMenuContent, 
  DropdownMenuItem, 
  DropdownMenuTrigger,
  DropdownMenuSeparator
} from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { 
  ShoppingCart, 
  User, 
  Package, 
  Settings, 
  LogOut,
  Menu,
  Store
} from "lucide-react";
import { useState } from "react";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import type { CartItem, Product, SiteSettings } from "@shared/schema";

type CartItemWithProduct = CartItem & { product: Product };

export function NavigationHeader() {
  const { user, logoutMutation } = useAuth();
  const { hasManagerAccess, isLoading: permissionsLoading } = usePermissions();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const { data: cartItems } = useQuery<CartItemWithProduct[]>({
    queryKey: ["/api/cart"],
    enabled: !!user,
  });



  const { data: siteSettings } = useQuery<SiteSettings>({
    queryKey: ["/api/settings"],
  });

  const cartCount = cartItems?.reduce((sum, item) => sum + item.quantity, 0) || 0;

  const handleLogout = () => {
    logoutMutation.mutate(undefined, {
      onSuccess: () => {
        toast({
          title: "Logged out successfully",
          description: "You have been logged out of your account",
        });
        setLocation("/auth");
      },
    });
  };

  const getNavigationItems = () => {
    const baseItems = [
      { label: "Dashboard", href: "/", icon: Store },
      { label: "Categories", href: "/categories", icon: Package },
      { label: "Products", href: "/products", icon: Package },
    ];

    // Only show "My Orders" for regular customers, not admin/manager
    if (!user?.isAdmin && !hasManagerAccess()) {
      baseItems.push({ label: "My Orders", href: "/orders", icon: Package });
    }

    return baseItems;
  };

  const NavigationItems = ({ mobile = false }) => (
    <>
      {getNavigationItems().map((item) => {
        const Icon = item.icon;
        return (
          <Button
            key={item.href}
            variant="ghost"
            className={`${mobile ? 'justify-start w-full' : ''} transition-colors duration-200 font-medium`}
            style={{ 
              color: siteSettings?.headerTextColor || '#64748b',
              '--hover-color': siteSettings?.tabTextColor || '#2563eb'
            } as React.CSSProperties & { '--hover-color': string }}
            onMouseEnter={(e) => {
              e.currentTarget.style.color = siteSettings?.tabTextColor || '#2563eb';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.color = siteSettings?.headerTextColor || '#64748b';
            }}
            onClick={() => {
              setLocation(item.href);
              if (mobile) setIsMobileMenuOpen(false);
            }}
          >
            {mobile && <Icon className="mr-2 h-4 w-4" />}
            {item.label}
          </Button>
        );
      })}
    </>
  );

  const logoComponent = (
    <div className="flex items-center space-x-2">
      {siteSettings?.logoUrl ? (
        <img
          src={siteSettings.logoUrl}
          alt={siteSettings.siteName || "InnovanceOrbit"}
          className="h-16 w-auto"
          onError={(e) => {
            // Fallback to text logo if image fails to load
            e.currentTarget.style.display = 'none';
            e.currentTarget.nextElementSibling?.classList.remove('hidden');
          }}
        />
      ) : null}
      <span className={`text-3xl font-bold ${siteSettings?.logoUrl ? 'hidden' : ''}`}>
        {siteSettings?.siteName || "InnovanceOrbit"}
      </span>
    </div>
  );

  return (
    <header 
      className="bg-white shadow-sm border-b sticky top-0 z-50 nav-header"
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="grid grid-cols-3 items-center h-20 sm:h-24">
          {/* Left Section - Navigation Tabs */}
          <div className="flex items-center justify-start">
            <nav className="hidden lg:flex space-x-4 xl:space-x-6">
              <NavigationItems />
            </nav>

            {/* Mobile Menu for left navigation */}
            <Sheet open={isMobileMenuOpen} onOpenChange={setIsMobileMenuOpen}>
              <SheetTrigger asChild>
                <Button 
                  variant="ghost" 
                  size="icon" 
                  className="lg:hidden"
                  style={{ color: siteSettings?.headerTextColor || '#374151' }}
                >
                  <Menu className="h-5 w-5" />
                </Button>
              </SheetTrigger>
              <SheetContent side="left" className="w-64">
                <div className="flex flex-col space-y-4 mt-6">
                  <NavigationItems mobile />
                </div>
              </SheetContent>
            </Sheet>
          </div>

          {/* Center Section - Logo */}
          <div className="flex items-center justify-center px-2">
            <Button
              variant="ghost"
              className="text-lg sm:text-xl lg:text-2xl font-bold hover:bg-transparent flex items-center space-x-2 sm:space-x-3 transition-colors duration-200 px-2"
              style={{ color: siteSettings?.headerTextColor || '#2563eb' }}
              onMouseEnter={(e) => {
                e.currentTarget.style.color = siteSettings?.tabTextColor || '#2563eb';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.color = siteSettings?.headerTextColor || '#2563eb';
              }}
              onClick={() => setLocation("/")}
            >
              {siteSettings?.logoUrl && siteSettings.logoUrl.trim() !== "" ? (
                <img 
                  src={siteSettings.logoUrl} 
                  alt={siteSettings.siteName || "Store"} 
                  className="w-auto max-w-[150px] sm:max-w-[200px]"
                  style={{ height: `${siteSettings.headerLogoHeight || 64}px` }}
                />
              ) : (!siteSettings?.siteName || siteSettings.siteName.trim() === "") ? (
                <Store className="w-8 h-8 sm:w-10 sm:h-10 lg:w-12 lg:h-12" />
              ) : null}
              {siteSettings?.siteName && siteSettings.siteName.trim() !== "" && (
                <span className="text-center truncate max-w-[120px] sm:max-w-[200px]">{siteSettings.siteName}</span>
              )}
            </Button>
          </div>

          {/* Right Section - User Actions */}
          <div className="flex items-center justify-end space-x-2 sm:space-x-4">


            {/* Shopping Cart - Only for regular customers, not admin/manager */}
            {!user?.isAdmin && !hasManagerAccess() && (
              <Button
                variant="ghost"
                size="icon"
                className="relative transition-colors duration-200"
                style={{ color: siteSettings?.headerTextColor || '#64748b' }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.color = siteSettings?.tabTextColor || '#2563eb';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.color = siteSettings?.headerTextColor || '#64748b';
                }}
                onClick={() => setLocation("/cart")}
              >
                <ShoppingCart className="h-4 w-4 sm:h-5 sm:w-5" />
                {cartCount > 0 && (
                  <Badge 
                    className="absolute -top-1 -right-1 sm:-top-2 sm:-right-2 text-xs px-1 sm:px-1.5 py-0.5 min-w-[1rem] sm:min-w-[1.25rem] h-4 sm:h-5 flex items-center justify-center"
                    style={{ 
                      backgroundColor: '#0ea5e9',
                      color: '#ffffff'
                    }}
                  >
                    {cartCount > 99 ? "99+" : cartCount}
                  </Badge>
                )}
              </Button>
            )}

            {/* User Menu */}
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" className="flex items-center space-x-1 sm:space-x-2 px-1 sm:px-3">
                  <Avatar className="h-6 w-6 sm:h-8 sm:w-8">
                    <AvatarFallback 
                      className="text-white text-xs sm:text-sm"
                      style={{ backgroundColor: '#2563eb' }}
                    >
                      {user?.username.charAt(0).toUpperCase() || "U"}
                    </AvatarFallback>
                  </Avatar>
                  <span 
                    className="hidden md:block font-medium text-sm"
                    style={{ color: siteSettings?.headerTextColor || '#374151' }}
                  >
                    {user?.username || "User"}
                  </span>
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end" className="w-56">
                {/* Show loading while permissions are being fetched */}
                {permissionsLoading && (
                  <>
                    <DropdownMenuItem disabled>
                      <Settings className="mr-2 h-4 w-4" />
                      Loading...
                    </DropdownMenuItem>
                    <DropdownMenuSeparator />
                  </>
                )}

                {/* Super Admin Panel */}
                {!permissionsLoading && user?.isSuperAdmin && (
                  <>
                    <DropdownMenuItem onClick={() => setLocation("/admin")}>
                      <Settings className="mr-2 h-4 w-4" />
                      Super Admin Panel
                    </DropdownMenuItem>
                    <DropdownMenuSeparator />
                  </>
                )}

                {/* Manager Panel */}
                {!permissionsLoading && !user?.isSuperAdmin && hasManagerAccess() && (
                  <>
                    <DropdownMenuItem onClick={() => setLocation("/admin")}>
                      <Settings className="mr-2 h-4 w-4" />
                      Manager Panel
                    </DropdownMenuItem>
                    <DropdownMenuSeparator />
                  </>
                )}

                {/* Regular User Panel */}
                {!permissionsLoading && !user?.isSuperAdmin && !hasManagerAccess() && (
                  <>
                    <DropdownMenuItem onClick={() => setLocation("/dashboard")}>
                      <User className="mr-2 h-4 w-4" />
                      User Panel
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => setLocation("/orders")}>
                      <Package className="mr-2 h-4 w-4" />
                      My Orders
                    </DropdownMenuItem>
                    <DropdownMenuSeparator />
                  </>
                )}
                <DropdownMenuItem 
                  onClick={handleLogout}
                  className="text-red-600 focus:text-red-600"
                  disabled={logoutMutation.isPending}
                >
                  {logoutMutation.isPending ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-2 border-red-500 border-t-transparent mr-2"></div>
                      Signing Out...
                    </>
                  ) : (
                    <>
                      <LogOut className="mr-2 h-4 w-4" />
                      Sign Out
                    </>
                  )}
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>

          </div>
        </div>
      </div>
    </header>
  );
}

client/src/components/order-payment-chec.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useLocation } from "wouter";
import { ArrowLeft, Package, CheckCircle, AlertCircle } from "lucide-react";
import { BahrainPaymentMethods } from "@/components/BahrainPaymentMethods";
import type { Order, OrderItem, Product } from "@shared/schema";

type OrderWithItems = Order & { items: (OrderItem & { product: Product })[] };

interface OrderPaymentCheckoutProps {
  orderId: string;
}

export function OrderPaymentCheckout({ orderId }: OrderPaymentCheckoutProps) {
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const { data: order, isLoading } = useQuery<OrderWithItems>({
    queryKey: [`/api/orders/${orderId}`],
  });

  const updateOrderMutation = useMutation({
    mutationFn: async (paymentData: any) => {
      const res = await apiRequest(`/api/orders/${orderId}/payment`, "PUT", paymentData);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/orders"] });
      queryClient.invalidateQueries({ queryKey: [`/api/orders/${orderId}`] });
      toast({
        title: "Payment Successful",
        description: "Your payment has been processed successfully.",
      });
      setLocation("/orders");
    },
    onError: () => {
      toast({
        title: "Payment Failed",
        description: "Please try again or contact support.",
        variant: "destructive",
      });
    },
  });

  const handlePaymentSuccess = (paymentData: any) => {
    updateOrderMutation.mutate({
      paymentMethod: paymentData.method,
      paymentIntentId: paymentData.transactionId || `ORD_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      status: "processing", // Update order status to processing after payment
    });
  };

  const handlePaymentError = (error: string) => {
    toast({
      title: "Payment Error",
      description: error,
      variant: "destructive",
    });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-slate-50">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="animate-pulse">
            <div className="h-8 bg-slate-200 rounded w-48 mb-8"></div>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <div className="space-y-4">
                <div className="h-64 bg-white rounded-lg"></div>
              </div>
              <div className="h-96 bg-white rounded-lg"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!order) {
    return (
      <div className="min-h-screen bg-slate-50">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center py-16">
            <AlertCircle className="w-16 h-16 text-red-500 mx-auto mb-4" />
            <h2 className="text-2xl font-bold text-slate-900 mb-4">Order Not Found</h2>
            <p className="text-slate-600 mb-8">The requested order could not be found.</p>
            <Button onClick={() => setLocation("/orders")}>
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back to Orders
            </Button>
          </div>
        </div>
      </div>
    );
  }

  // Check if order can proceed to payment
  const adminApprovalStatus = (order as any).adminApprovalStatus;
  const canProceedToPayment = adminApprovalStatus === "approved" && 
                              (order.status === "awaiting_approval" || order.status === "payment_pending");

  if (!canProceedToPayment) {
    return (
      <div className="min-h-screen bg-slate-50">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center py-16">
            <AlertCircle className="w-16 h-16 text-yellow-500 mx-auto mb-4" />
            <h2 className="text-2xl font-bold text-slate-900 mb-4">Payment Not Available</h2>
            <p className="text-slate-600 mb-8">
              {adminApprovalStatus === "pending" 
                ? "This order is still awaiting admin approval." 
                : adminApprovalStatus === "rejected"
                ? "This order has been rejected by admin."
                : "Payment is not available for this order at this time."}
            </p>
            <Button onClick={() => setLocation("/orders")}>
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back to Orders
            </Button>
          </div>
        </div>
      </div>
    );
  }

  const total = parseFloat(order.total);

  return (
    <div className="min-h-screen bg-slate-50">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center space-x-4">
            <Button
              variant="ghost"
              onClick={() => setLocation("/orders")}
              className="flex items-center space-x-2 text-slate-600 hover:text-slate-900"
            >
              <ArrowLeft className="h-4 w-4" />
              <span>Back to Orders</span>
            </Button>
            <div>
              <h1 className="text-3xl font-bold text-slate-900">Complete Payment</h1>
              <p className="text-slate-600 mt-1">Order #{order.id.slice(-8).toUpperCase()}</p>
            </div>
          </div>
          <Badge className="bg-green-100 text-green-800 border-green-200">
            <CheckCircle className="h-4 w-4 mr-1" />
            Approved
          </Badge>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Order Summary */}
          <div className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Order Summary</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {/* Order Items */}
                  <div>
                    <h4 className="font-medium text-slate-900 mb-3">Items ({order.items?.length || 0})</h4>
                    <div className="space-y-3">
                      {order.items?.map((item) => (
                        <div key={item.id} className="flex items-center justify-between">
                          <div className="flex items-center space-x-3">
                            {item.product.imageUrl ? (
                              <img 
                                src={item.product.imageUrl} 
                                alt={item.product.name}
                                className="w-12 h-12 object-cover rounded border"
                              />
                            ) : (
                              <div className="w-12 h-12 bg-gradient-to-br from-blue-100 to-purple-100 rounded flex items-center justify-center border">
                                <span className="text-slate-600 font-semibold text-sm">
                                  {item.product.name.charAt(0)}
                                </span>
                              </div>
                            )}
                            <div>
                              <p className="font-medium text-slate-900">{item.product.name}</p>
                              <div className="text-sm text-slate-600 space-y-1">
                                <p>Qty: {item.quantity}</p>
                                {item.rentalStartDate && item.rentalEndDate && (
                                  <p>
                                    Rental: {new Date(item.rentalStartDate).toLocaleDateString()} - {new Date(item.rentalEndDate).toLocaleDateString()}
                                  </p>
                                )}
                                {item.rentalDays && (
                                  <p>Duration: {item.rentalDays} day{item.rentalDays > 1 ? 's' : ''}</p>
                                )}
                              </div>
                            </div>
                          </div>
                          <div className="text-right">
                            <div className="text-sm text-slate-600 mb-1">
                              ${parseFloat(item.price).toFixed(2)} √ó {item.quantity}
                            </div>
                            <p className="font-medium text-slate-900">
                              ${item.totalPrice || (parseFloat(item.price) * item.quantity).toFixed(2)}
                            </p>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Order Totals */}
                  <div className="border-t pt-4 space-y-2">
                    <div className="flex justify-between text-sm text-slate-600">
                      <span>Subtotal</span>
                      <span>${order.subtotal}</span>
                    </div>
                    <div className="flex justify-between text-sm text-slate-600">
                      <span>Tax (10% VAT)</span>
                      <span>${order.tax}</span>
                    </div>
                    <div className="flex justify-between text-lg font-semibold text-slate-900 border-t pt-2">
                      <span>Total</span>
                      <span>${order.total}</span>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>


          </div>

          {/* Payment Section */}
          <div>
            <BahrainPaymentMethods
              total={total}
              orderId={order.id}
              shippingData={(order as any).shippingAddress}
              onPaymentSuccess={handlePaymentSuccess}
              onPaymentError={handlePaymentError}
              isLocked={false}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/product-card.tsx.content:
import { useMutation, useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Star, ShoppingCart, Eye } from "lucide-react";
import { useLocation } from "wouter";

import type { Product } from "@shared/schema";

interface ProductCardProps {
  product: Product;
  onViewDetails?: (product: Product) => void;
  onCardClick?: (product: Product) => void;
  showDetailsButton?: boolean;
}

export function ProductCard({ product, onViewDetails, onCardClick, showDetailsButton = true }: ProductCardProps) {
  const { toast } = useToast();
  const [, setLocation] = useLocation();


  const addToCartMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest("/api/cart", "POST", {
        productId: product.id,
        quantity: 1,
      });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Added to cart",
        description: `${product.name} has been added to your cart`,
      });
      // Redirect to cart page after successful add
      setLocation("/cart");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add item to cart",
        variant: "destructive",
      });
    },
  });

  const handleAddToCart = (e: React.MouseEvent) => {
    e.stopPropagation();

    // For rental products, redirect to product detail page to select dates
    if (product.productType === "rental") {
      setLocation(`/products/${product.id}`);
      return;
    }

    addToCartMutation.mutate();
  };



  const renderStars = (rating: number) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(
        <Star key={i} className="w-4 h-4 fill-yellow-400 text-yellow-400" />
      );
    }

    if (hasHalfStar) {
      stars.push(
        <div key="half" className="relative">
          <Star className="w-4 h-4 text-gray-300" />
          <div className="absolute inset-0 overflow-hidden w-1/2">
            <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
          </div>
        </div>
      );
    }

    const emptyStars = 5 - Math.ceil(rating);
    for (let i = 0; i < emptyStars; i++) {
      stars.push(
        <Star key={`empty-${i}`} className="w-4 h-4 text-gray-300" />
      );
    }

    return stars;
  };

  const rating = parseFloat(product.rating || "0");
  const reviewCount = product.reviewCount || 0;

  const handleCardClick = () => {
    if (onCardClick) {
      onCardClick(product);
    }
  };

  return (
    <Card 
      className="group cursor-pointer overflow-hidden border-none shadow-md hover:shadow-2xl transition-all duration-500 hover:scale-[1.02] bg-gradient-to-br from-white to-slate-50 flex flex-col"
      onClick={handleCardClick}
    >
      <div className="relative overflow-hidden rounded-t-xl">
        {product.imageUrl ? (
          <img 
            src={product.imageUrl} 
            alt={product.name}
            className="w-full h-56 object-cover group-hover:scale-110 transition-transform duration-500"
          />
        ) : (
          <div className="w-full h-56 bg-gradient-to-br from-blue-100 via-purple-50 to-pink-100 flex items-center justify-center group-hover:scale-110 transition-transform duration-500">
            <span className="text-slate-600 text-2xl font-bold">
              {product.name.charAt(0)}
            </span>
          </div>
        )}

        {/* Badges */}
        <div className="absolute top-3 left-3">
          {product.isFeatured && (
            <Badge className="bg-gradient-to-r from-emerald-500 to-green-600 text-white text-xs px-3 py-1.5 font-semibold shadow-lg">
              ‚≠ê Featured
            </Badge>
          )}
        </div>




      </div>

      <CardContent className="p-6 bg-gradient-to-b from-transparent to-slate-50/30 flex flex-col">
        <div className="flex-1">
          <h3 className="text-xl font-bold text-slate-900 mb-3 line-clamp-2 group-hover:text-blue-700 transition-colors duration-300">
            {product.name}
          </h3>

          <p className="text-slate-600 text-sm mb-4 line-clamp-2 leading-relaxed">
            {product.description || "No description available"}
          </p>

          <div className="flex items-center justify-between mb-4">
            <span className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              ${parseFloat(product.price).toFixed(2)}
            </span>

            {rating > 0 && (
              <div className="flex items-center space-x-2 bg-yellow-50 px-3 py-1.5 rounded-full border border-yellow-200">
                <div className="flex">
                  {renderStars(rating)}
                </div>
                <span className="text-slate-600 text-sm font-medium">
                  ({reviewCount > 0 ? reviewCount : rating.toFixed(1)})
                </span>
              </div>
            )}
          </div>
        </div>

        <div className="mt-auto">
          {showDetailsButton ? (
            <div className="flex gap-2">
              <Button 
                variant="outline"
                className="flex-1 border-2 border-blue-200 hover:border-blue-400 text-blue-600 hover:text-blue-700 font-semibold py-3 px-4 rounded-xl shadow-md hover:shadow-lg transition-all duration-300"
                onClick={(e) => {
                  e.stopPropagation();
                  onViewDetails?.(product);
                }}
              >
                <Eye className="mr-2 h-4 w-4" />
                Details
              </Button>

              <Button 
                className="flex-1 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white font-semibold py-3 px-4 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105 group disabled:opacity-50 disabled:hover:scale-100"
                onClick={handleAddToCart}
                disabled={addToCartMutation.isPending}
              >
                {addToCartMutation.isPending ? (
                  <div className="flex items-center">
                    <div className="animate-spin rounded-full h-4 w-4 border-2 border-white border-t-transparent mr-1"></div>
                    Adding...
                  </div>
                ) : (
                  <>
                    <ShoppingCart className="mr-1 h-4 w-4 group-hover:animate-bounce" />
                    Add to Cart
                  </>
                )}
              </Button>
            </div>
          ) : (
            <Button 
              className="w-full bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white font-semibold py-3 px-4 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105 group disabled:opacity-50 disabled:hover:scale-100"
              onClick={handleAddToCart}
              disabled={addToCartMutation.isPending}
            >
              {addToCartMutation.isPending ? (
                <div className="flex items-center">
                  <div className="animate-spin rounded-full h-4 w-4 border-2 border-white border-t-transparent mr-1"></div>
                  Adding...
                </div>
              ) : (
                <>
                  <ShoppingCart className="mr-2 h-5 w-5 group-hover:animate-bounce" />
                  Add to Cart
                </>
              )}
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

