import { toast } from "@/hooks/use-toast";

export interface ImageUploadOptions {
  onUploadStart?: () => void;
  onUploadComplete?: (imageUrl: string) => void;
  onUploadError?: (error: string) => void;
}

export async function uploadImage(
  file: File,
  options: ImageUploadOptions = {}
): Promise<string | null> {
  const { onUploadStart, onUploadComplete, onUploadError } = options;

  if (!file) {
    const error = "No file selected";
    onUploadError?.(error);
    toast({
      title: "Error",
      description: error,
      variant: "destructive",
    });
    return null;
  }

  // Validate file type
  if (!file.type.startsWith("image/")) {
    const error = "Please select an image file";
    onUploadError?.(error);
    toast({
      title: "Error", 
      description: error,
      variant: "destructive",
    });
    return null;
  }

  // Validate file size (5MB limit)
  const maxSize = 5 * 1024 * 1024; // 5MB in bytes
  if (file.size > maxSize) {
    const error = "Image size must be less than 5MB";
    onUploadError?.(error);
    toast({
      title: "Error",
      description: error,
      variant: "destructive",
    });
    return null;
  }

  try {
    onUploadStart?.();

    const formData = new FormData();
    formData.append("image", file);

    const response = await fetch("/api/upload-image", {
      method: "POST",
      body: formData,
      credentials: "include",
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || "Upload failed");
    }

    const data = await response.json();
    const imageUrl = data.imageUrl;

    onUploadComplete?.(imageUrl);
    toast({
      title: "Success",
      description: "Image uploaded successfully",
    });

    return imageUrl;
  } catch (error: any) {
    const errorMessage = error.message || "Failed to upload image";
    onUploadError?.(errorMessage);
    toast({
      title: "Error",
      description: errorMessage,
      variant: "destructive",
    });
    return null;
  }
}

export function createImageUploadHandler(
  onImageUrlChange: (url: string) => void,
  setIsUploading?: (uploading: boolean) => void
) {
  return async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const imageUrl = await uploadImage(file, {
      onUploadStart: () => setIsUploading?.(true),
      onUploadComplete: (url) => {
        onImageUrlChange(url);
        setIsUploading?.(false);
      },
      onUploadError: () => setIsUploading?.(false),
    });
  };
}

client/src/lib/protected-route.tsx.content:
import { useAuth } from "@/hooks/use-auth";
import { Loader2 } from "lucide-react";
import { Redirect, Route } from "wouter";

export function ProtectedRoute({
  path,
  component: Component,
}: {
  path: string;
  component: () => React.JSX.Element;
}) {
  const { user, isLoading } = useAuth();

  if (isLoading) {
    return (
      <Route path={path}>
        <div className="flex items-center justify-center min-h-screen">
          <Loader2 className="h-8 w-8 animate-spin text-border" />
        </div>
      </Route>
    );
  }

  if (!user) {
    return (
      <Route path={path}>
        <Redirect to="/auth" />
      </Route>
    );
  }

  return (
    <Route path={path}>
      <Component />
    </Route>
  );
}

client/src/lib/queryClient.ts.content:
import { QueryClient, QueryFunction } from "@tanstack/react-query";

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    const text = (await res.text()) || res.statusText;
    throw new Error(`${res.status}: ${text}`);
  }
}

export async function apiRequest(
  url: string,
  method: string,
  data?: unknown | undefined,
): Promise<Response> {
  const res = await fetch(url, {
    method,
    headers: data ? { "Content-Type": "application/json" } : {},
    body: data ? JSON.stringify(data) : undefined,
    credentials: "include",
  });

  await throwIfResNotOk(res);
  return res;
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
  ({ on401: unauthorizedBehavior }) =>
  async ({ queryKey }) => {
    const res = await fetch(queryKey.join("/") as string, {
      credentials: "include",
    });

    if (unauthorizedBehavior === "returnNull" && res.status === 401) {
      return null;
    }

    await throwIfResNotOk(res);
    return await res.json();
  };

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: getQueryFn({ on401: "throw" }),
      refetchInterval: false,
      refetchOnWindowFocus: false,
      staleTime: Infinity,
      retry: false,
    },
    mutations: {
      retry: false,
    },
  },
});

