import { useParams } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { Card, CardContent } from "@/components/ui/card";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Calendar as CalendarComponent } from "@/components/ui/calendar";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useMutation } from "@tanstack/react-query";
import { useState } from "react";
import { useLocation } from "wouter";
import { format, differenceInDays } from "date-fns";
import { 
  Star, 
  ShoppingCart, 
  Package, 
  Tag, 
  Info, 
  Calendar,
  Calendar as CalendarIcon,
  DollarSign,
  Minus,
  Plus,
  ArrowLeft
} from "lucide-react";
import type { Product } from "@shared/schema";

export default function ProductDetailPage() {
  const { id } = useParams<{ id: string }>();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [quantity, setQuantity] = useState(1);

  // Rental date state
  const [startDate, setStartDate] = useState<Date | undefined>();
  const [endDate, setEndDate] = useState<Date | undefined>();
  const [dateError, setDateError] = useState("");
  const [isSingleDay, setIsSingleDay] = useState(false);

  // Rental period constants - ensure proper date boundaries
  const RENTAL_START = new Date(2025, 9, 18, 0, 0, 0); // October 18, 2025 00:00:00
  const RENTAL_END = new Date(2025, 9, 31, 23, 59, 59); // October 31, 2025 23:59:59

  const { data: product, isLoading } = useQuery<Product>({
    queryKey: [`/api/products/${id}`],
    enabled: !!id,
  });

  const addToCartMutation = useMutation({
    mutationFn: async ({ productId, quantity, rentalStartDate, rentalEndDate }: { 
      productId: string; 
      quantity: number;
      rentalStartDate?: string;
      rentalEndDate?: string;
    }) => {
      const res = await apiRequest("/api/cart", "POST", { 
        productId, 
        quantity,
        rentalStartDate,
        rentalEndDate
      });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Added to Cart",
        description: `${product?.name} has been added to your cart.`,
      });
      // Redirect to cart page after successful add
      setLocation("/cart");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add item to cart",
        variant: "destructive",
      });
    },
  });

  // Date validation function
  const validateDateRange = (start: Date, end: Date): string => {
    if (start < RENTAL_START || start > RENTAL_END || end < RENTAL_START || end > RENTAL_END) {
      return "Selected dates are outside the allowed rental period. Please choose dates between 18th October and 31st October 2025.";
    }
    // For single day rentals, allow start and end to be the same
    if (!isSingleDay && start >= end) {
      return "End date must be after start date.";
    }
    // For single day rentals, start and end should be the same
    if (isSingleDay && start.getTime() !== end.getTime()) {
      return "For single day rental, start and end dates must be the same.";
    }
    return "";
  };

  // Calculate rental days and total cost
  const calculateRentalCost = (): { days: number; totalCost: number; dailyRate: number } => {
    if (!startDate || !endDate || !product?.rentalPrice) {
      return { days: 0, totalCost: 0, dailyRate: 0 };
    }

    const days = differenceInDays(endDate, startDate) + 1; // Include both start and end dates
    const dailyRate = parseFloat(product.rentalPrice);
    const totalCost = days * dailyRate * quantity;

    return { days, totalCost, dailyRate };
  };

  // Handle date selection
  const handleStartDateSelect = (date: Date | undefined) => {
    if (!date) return;

    setStartDate(date);
    setDateError("");

    // For single day rental, set end date to start date
    if (isSingleDay) {
      setEndDate(date);
    } else if (endDate) {
      // If we have both dates, validate them
      const error = validateDateRange(date, endDate);
      setDateError(error);
    }
  };

  const handleEndDateSelect = (date: Date | undefined) => {
    if (!date || isSingleDay) return; // Prevent end date selection in single day mode

    setEndDate(date);
    setDateError("");

    // If we have both dates, validate them
    if (startDate) {
      const error = validateDateRange(startDate, date);
      setDateError(error);
    }
  };

  const handleSingleDayToggle = (checked: boolean) => {
    setIsSingleDay(checked);
    if (checked && startDate) {
      setEndDate(startDate); // Set end date to start date for single day
      setDateError(""); // Clear any existing errors
    } else if (!checked && startDate && endDate && startDate.getTime() === endDate.getTime()) {
      setEndDate(undefined); // Clear end date if it was same as start date
      setDateError("");
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-slate-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="animate-pulse">
            <div className="h-8 bg-gray-200 rounded w-32 mb-8"></div>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <div className="aspect-square bg-gray-200 rounded-lg"></div>
              <div className="space-y-4">
                <div className="h-8 bg-gray-200 rounded w-3/4"></div>
                <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                <div className="h-20 bg-gray-200 rounded"></div>
                <div className="h-16 bg-gray-200 rounded"></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen bg-slate-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <Button variant="outline" onClick={() => setLocation("/products")} className="mb-8">
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Products
          </Button>
          <div className="text-center py-12">
            <h1 className="text-2xl font-bold text-gray-900 mb-4">Product Not Found</h1>
            <p className="text-gray-600">The product you're looking for doesn't exist.</p>
          </div>
        </div>
      </div>
    );
  }

  const handleAddToCart = () => {
    // Validate rental dates if product is rental
    if (product.productType === "rental") {
      if (!startDate || !endDate) {
        toast({
          title: "Dates Required",
          description: "Please select start and end dates for rental period.",
          variant: "destructive",
        });
        return;
      }

      if (dateError) {
        toast({
          title: "Invalid Dates",
          description: dateError,
          variant: "destructive",
        });
        return;
      }

    }

    // Format dates as YYYY-MM-DD without timezone conversion
    const formatDateForServer = (date: Date) => {
      const year = date.getFullYear();
      const month = String(date.getMonth() + 1).padStart(2, '0');
      const day = String(date.getDate()).padStart(2, '0');
      return `${year}-${month}-${day}`;
    };

    const startDateString = startDate ? formatDateForServer(startDate) : undefined;
    const endDateString = endDate ? formatDateForServer(endDate) : undefined;



    addToCartMutation.mutate({
      productId: product.id,
      quantity,
      rentalStartDate: startDateString,
      rentalEndDate: endDateString
    });
  };

  const incrementQuantity = () => {
    setQuantity(quantity + 1);
  };

  const decrementQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  const renderStars = (rating: number) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(
        <Star key={i} className="w-4 h-4 fill-yellow-400 text-yellow-400" />
      );
    }

    if (hasHalfStar) {
      stars.push(
        <div key="half" className="relative">
          <Star className="w-4 h-4 text-gray-300" />
          <div className="absolute inset-0 overflow-hidden w-1/2">
            <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
          </div>
        </div>
      );
    }

    const emptyStars = 5 - Math.ceil(rating);
    for (let i = 0; i < emptyStars; i++) {
      stars.push(
        <Star key={`empty-${i}`} className="w-4 h-4 text-gray-300" />
      );
    }

    return stars;
  };

  const rating = parseFloat(product.rating || "0");

  return (
    <div className="min-h-screen bg-slate-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <Button variant="outline" onClick={() => setLocation("/products")} className="mb-8">
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Products
        </Button>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          {/* Product Image */}
          <div className="space-y-4">
            <div className="aspect-square bg-white rounded-xl overflow-hidden shadow-lg">
              {product.imageUrl ? (
                <img 
                  src={product.imageUrl} 
                  alt={product.name}
                  className="w-full h-full object-cover"
                />
              ) : (
                <div className="w-full h-full flex items-center justify-center bg-gradient-to-br from-gray-100 to-gray-200">
                  <Package className="w-24 h-24 text-gray-400" />
                </div>
              )}
            </div>

            {/* Product Badges */}
            <div className="flex flex-wrap gap-2">
              {product.isFeatured && (
                <Badge variant="default" className="bg-yellow-500">
                  ‚≠ê Featured
                </Badge>
              )}
              <Badge variant={product.productType === "sale" ? "default" : "secondary"}>
                {product.productType === "sale" ? "For Sale" : "For Rent"}
              </Badge>
              <Badge variant="outline" className="text-green-600 border-green-600">
                Available
              </Badge>
            </div>
          </div>

          {/* Product Information */}
          <div className="space-y-8">
            <div>
              <h1 className="text-4xl font-bold text-gray-900 mb-4">{product.name}</h1>

              {/* Rating */}
              {rating > 0 && (
                <div className="flex items-center gap-2 mb-6">
                  <div className="flex items-center">
                    {renderStars(rating)}
                  </div>
                  <span className="text-sm text-gray-600">
                    {product.rating} ({product.reviewCount} reviews)
                  </span>
                </div>
              )}
            </div>

            {/* Pricing */}
            <Card>
              <CardContent className="p-6">
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <span className="text-xl font-semibold text-gray-700">Sale Price:</span>
                    <span className="text-3xl font-bold text-primary">${product.price}</span>
                  </div>

                  {product.productType === "rental" && product.rentalPrice && (
                    <div className="flex items-center justify-between border-t pt-4">
                      <span className="text-xl font-semibold text-gray-700">Rental Price:</span>
                      <div className="text-right">
                        <span className="text-2xl font-bold text-blue-600">${product.rentalPrice}</span>
                        {product.rentalPeriod && (
                          <p className="text-sm text-gray-600">per {product.rentalPeriod}</p>
                        )}
                      </div>
                    </div>
                  )}

                  {/* VAT Notice */}
                  <div className="bg-blue-50 p-4 rounded-lg text-sm text-blue-800">
                    <Info className="w-4 h-4 inline mr-2" />
                    Prices exclude 10% VAT
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Description */}
            <div>
              <h3 className="text-2xl font-semibold text-gray-900 mb-4">Description</h3>
              <p className="text-gray-600 leading-relaxed text-lg">
                {product.description || "No description available for this product."}
              </p>
            </div>

            {/* Rental Date Selection */}
            {product.productType === "rental" && (
              <Card>
                <CardContent className="p-6">
                  <h3 className="text-2xl font-semibold text-gray-900 mb-4">Select Rental Period</h3>
                  <div className="space-y-4">
                    <div className="bg-amber-50 border border-amber-200 rounded-lg p-4">
                      <p className="text-sm text-amber-800">
                        <Calendar className="w-4 h-4 inline mr-2" />
                        Products rental only between 18th October and 31st October 2025.
                        You can select dates for single day or multiple day rentals. Pricing will be automatically calculated based on the number of days selected.
                      </p>
                    </div>

                    {/* Single Day Option */}
                    <div className="flex items-center space-x-2 p-4 bg-blue-50 border border-blue-200 rounded-lg">
                      <Checkbox 
                        id="single-day" 
                        checked={isSingleDay}
                        onCheckedChange={handleSingleDayToggle}
                      />
                      <label 
                        htmlFor="single-day" 
                        className="text-sm font-medium text-blue-800 cursor-pointer flex-1"
                      >
                        Single day rental (just pick one date)
                      </label>
                      {isSingleDay && (
                        <span className="text-xs text-blue-600 bg-blue-100 px-2 py-1 rounded">
                          Only start date needed
                        </span>
                      )}
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {/* Start Date Picker */}
                      <div className="space-y-2">
                        <label className="text-sm font-medium text-gray-700">Start Date</label>
                        <Popover>
                          <PopoverTrigger asChild>
                            <Button
                              variant="outline"
                              className="w-full justify-start text-left font-normal"
                            >
                              <CalendarIcon className="mr-2 h-4 w-4" />
                              {startDate ? format(startDate, "PPP") : "Select start date"}
                            </Button>
                          </PopoverTrigger>
                          <PopoverContent className="w-auto p-0" align="start">
                            <CalendarComponent
                              mode="single"
                              selected={startDate}
                              onSelect={handleStartDateSelect}
                              disabled={(date) => {
                                const today = new Date();
                                today.setHours(0, 0, 0, 0);
                                const checkDate = new Date(date);
                                checkDate.setHours(0, 0, 0, 0);

                                if (checkDate < RENTAL_START || checkDate > RENTAL_END || checkDate < today) {
                                  return true;
                                }
                                return false;
                              }}
                              initialFocus
                            />
                          </PopoverContent>
                        </Popover>
                      </div>

                      {/* End Date Picker */}
                      <div className={`space-y-2 ${isSingleDay ? 'opacity-50 pointer-events-none' : ''}`}>
                        <label className="text-sm font-medium text-gray-700">
                          End Date {isSingleDay && "(Not needed for single day)"}
                        </label>
                        <Popover>
                          <PopoverTrigger asChild>
                            <Button
                              variant="outline"
                              className="w-full justify-start text-left font-normal"
                            >
                              <CalendarIcon className="mr-2 h-4 w-4" />
                              {endDate ? format(endDate, "PPP") : "Select end date"}
                            </Button>
                          </PopoverTrigger>
                          <PopoverContent className="w-auto p-0" align="start">
                            <CalendarComponent
                              mode="single"
                              selected={endDate}
                              onSelect={handleEndDateSelect}
                              disabled={(date) => {
                                const today = new Date();
                                today.setHours(0, 0, 0, 0);
                                const checkDate = new Date(date);
                                checkDate.setHours(0, 0, 0, 0);

                                if (checkDate < RENTAL_START || checkDate > RENTAL_END || checkDate < today) {
                                  return true;
                                }
                                if (startDate && checkDate <= startDate) {
                                  return true;
                                }
                                return false;
                              }}
                              initialFocus
                            />
                          </PopoverContent>
                        </Popover>
                      </div>
                    </div>

                    {/* Date Error Display */}
                    {dateError && (
                      <div className="bg-red-50 border border-red-200 rounded-lg p-3">
                        <p className="text-sm text-red-800">‚ùó {dateError}</p>
                      </div>
                    )}

                    {/* Rental Cost Display */}
                    {startDate && endDate && !dateError && (
                      <div className="bg-green-50 border border-green-200 rounded-lg p-4">
                        <h4 className="font-semibold text-green-800 mb-2">Rental Summary</h4>
                        <div className="grid grid-cols-1 sm:grid-cols-3 gap-3 text-sm">
                          <div>
                            <span className="text-green-700">Duration:</span>
                            <p className="font-medium text-green-900">{calculateRentalCost().days} days</p>
                          </div>
                          <div>
                            <span className="text-green-700">Daily Rate:</span>
                            <p className="font-medium text-green-900">${calculateRentalCost().dailyRate}</p>
                          </div>
                          <div>
                            <span className="text-green-700">Total Cost:</span>
                            <p className="font-bold text-green-900">${calculateRentalCost().totalCost.toFixed(2)}</p>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Product Details */}
            <Card>
              <CardContent className="p-6">
                <h3 className="text-2xl font-semibold text-gray-900 mb-4">Product Information</h3>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                  <div className="flex items-center gap-3">
                    <Tag className="w-5 h-5 text-gray-500" />
                    <span className="text-gray-600">SKU:</span>
                    <span className="font-medium">{product.sku || 'N/A'}</span>
                  </div>
                  <div className="flex items-center gap-3">
                    <Package className="w-5 h-5 text-gray-500" />
                    <span className="text-gray-600">Unit:</span>
                    <span className="font-medium">{product.unitOfMeasure || 'piece'}</span>
                  </div>
                  <div className="flex items-center gap-3">
                    <DollarSign className="w-5 h-5 text-gray-500" />
                    <span className="text-gray-600">Type:</span>
                    <span className="font-medium capitalize">{product.productType}</span>
                  </div>
                  <div className="flex items-center gap-3">
                    <Calendar className="w-5 h-5 text-gray-500" />
                    <span className="text-gray-600">Added:</span>
                    <span className="font-medium">
                      {product.createdAt ? new Date(product.createdAt).toLocaleDateString() : 'N/A'}
                    </span>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Add to Cart Section */}
            <Card>
                <CardContent className="p-6">
                  <div className="space-y-6">
                    <div className="flex items-center gap-4">
                      <span className="text-lg font-medium text-gray-700">Quantity:</span>
                      <div className="flex items-center gap-3">
                        <Button
                          size="sm"
                          variant="outline"
                          className="h-10 w-10 p-0"
                          onClick={decrementQuantity}
                          disabled={quantity <= 1}
                        >
                          <Minus className="h-4 w-4" />
                        </Button>
                        <span className="w-16 text-center font-medium text-lg">{quantity}</span>
                        <Button
                          size="sm"
                          variant="outline"
                          className="h-10 w-10 p-0"
                          onClick={incrementQuantity}
                          disabled={false}
                        >
                          <Plus className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>

                    <div className="flex items-center justify-between pt-4 border-t">
                      <span className="text-2xl font-semibold">
                        Total: ${(() => {
                          if (product.productType === "rental" && startDate && endDate && !dateError) {
                            return calculateRentalCost().totalCost.toFixed(2);
                          }
                          return (parseFloat(product.price) * quantity).toFixed(2);
                        })()}
                      </span>
                      <Button 
                        onClick={handleAddToCart}
                        disabled={addToCartMutation.isPending}
                        className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105"
                        size="lg"
                      >
                        <ShoppingCart className="w-5 h-5 mr-2" />
                        {addToCartMutation.isPending ? "Adding..." : "Add to Cart"}
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>


          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/products-page.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { ProductCard } from "@/components/product-card";

import { Card, CardContent } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import type { Product, Category } from "@shared/schema";

export default function ProductsPage() {
  const [location, setLocation] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>("all");


  const { data: products, isLoading: productsLoading } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  const { data: categories } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  // Handle category filtering from URL parameters
  useEffect(() => {
    const urlParams = new URLSearchParams(location.split('?')[1] || '');
    const categoryParam = urlParams.get('category');
    if (categoryParam) {
      setSelectedCategory(categoryParam);
    } else {
      setSelectedCategory("all");
    }
  }, [location]);

  // Get the selected category name for display
  const selectedCategoryName = selectedCategory !== "all" && categories 
    ? categories.find(c => c.id === selectedCategory)?.name 
    : null;

  const filteredProducts = products?.filter((product) => {
    const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         product.description?.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === "all" || product.categoryId === selectedCategory;
    return matchesSearch && matchesCategory;
  }) || [];



  return (
    <div className="bg-slate-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <div className="flex items-center justify-between mb-4">
            <h1 className="text-3xl font-bold text-slate-900">
              {selectedCategory !== "all" && categories 
                ? `${categories.find(c => c.id === selectedCategory)?.name || ""} Products`
                : "Products"
              }
            </h1>
            {selectedCategoryName && (
              <div className="hidden sm:flex items-center text-sm text-slate-600">
                <svg className="w-4 h-4 mr-2" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M3 3a1 1 0 011-1h12a1 1 0 011 1v3a1 1 0 01-.293.707L12 11.414V15a1 1 0 01-.293.707l-2 2A1 1 0 018 17v-5.586L3.293 6.707A1 1 0 013 6V3z" clipRule="evenodd" />
                </svg>
                Filtered by: <span className="font-medium ml-1">{selectedCategoryName}</span>
              </div>
            )}
          </div>

          {/* Filters */}
          <div className="flex flex-col sm:flex-row gap-4 mb-8">
            <div className="flex-1 relative">
              <Input
                placeholder={selectedCategoryName ? `Search in ${selectedCategoryName}...` : "Search products..."}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full"
              />
              {selectedCategoryName && (
                <div className="absolute right-3 top-1/2 transform -translate-y-1/2 pointer-events-none">
                  <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800 shadow-sm">
                    <svg className="w-3 h-3 mr-1" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M3 3a1 1 0 011-1h12a1 1 0 011 1v3a1 1 0 01-.293.707L12 11.414V15a1 1 0 01-.293.707l-2 2A1 1 0 018 17v-5.586L3.293 6.707A1 1 0 013 6V3z" clipRule="evenodd" />
                    </svg>
                    {selectedCategoryName}
                  </span>
                </div>
              )}
            </div>
            <div className="sm:w-48">
              <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                <SelectTrigger>
                  <SelectValue placeholder="All Categories" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  {categories?.map((category) => (
                    <SelectItem key={category.id} value={category.id}>
                      {category.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>

        {productsLoading ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
            {[...Array(8)].map((_, i) => (
              <Card key={i} className="overflow-hidden">
                <Skeleton className="h-48 w-full" />
                <CardContent className="p-6">
                  <Skeleton className="h-6 w-3/4 mb-2" />
                  <Skeleton className="h-4 w-full mb-4" />
                  <Skeleton className="h-8 w-20 mb-4" />
                  <Skeleton className="h-10 w-full" />
                </CardContent>
              </Card>
            ))}
          </div>
        ) : filteredProducts.length > 0 ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {filteredProducts.map((product) => (
              <ProductCard 
                key={product.id} 
                product={product}
                onCardClick={(product) => setLocation(`/products/${product.id}`)}
                showDetailsButton={false}
              />
            ))}
          </div>
        ) : (
          <div className="text-center py-12">
            <p className="text-slate-600">
              {searchQuery || selectedCategory !== "all" 
                ? "No products found matching your criteria." 
                : "No products available at the moment."}
            </p>
          </div>
        )}
      </div>



    </div>
  );
}

client/src/pages/profile-page.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Skeleton } from "@/components/ui/skeleton";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { 
  User, 
  Mail,
  Edit,
  Save,
  ArrowLeft,
  Lock
} from "lucide-react";

export default function ProfilePage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
  });

  // Fetch user data
  const { data: user, isLoading: userLoading } = useQuery<any>({
    queryKey: ["/api/user"],
  });

  // Update form data when user data loads
  useEffect(() => {
    if (user) {
      setFormData({
        firstName: user?.firstName || "",
        lastName: user?.lastName || "",
        email: user?.email || "",
      });
    }
  }, [user]);

  // Update profile mutation
  const updateProfileMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      return await apiRequest(`/api/user/profile`, "PATCH", data);
    },
    onSuccess: () => {
      toast({
        title: "Profile Updated",
        description: "Your profile has been updated successfully.",
      });
      setIsEditing(false);
      queryClient.invalidateQueries({ queryKey: ["/api/user"] });
    },
    onError: (error: any) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update profile.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateProfileMutation.mutate(formData);
  };

  const handleCancel = () => {
    setFormData({
      firstName: user?.firstName || "",
      lastName: user?.lastName || "",
      email: user?.email || "",
    });
    setIsEditing(false);
  };

  if (userLoading) {
    return (
      <div className="container mx-auto p-4 max-w-2xl">
        <div className="space-y-6">
          <div className="flex items-center gap-4">
            <Skeleton className="h-10 w-10" />
            <Skeleton className="h-8 w-32" />
          </div>
          <Skeleton className="h-96" />
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4 max-w-2xl">
      {/* Header */}
      <div className="flex items-center gap-4 mb-6">
        <Button 
          variant="ghost" 
          size="sm"
          onClick={() => setLocation("/dashboard")}
          className="p-2"
        >
          <ArrowLeft className="h-4 w-4" />
        </Button>
        <div>
          <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100">
            User Profile
          </h1>
          <p className="text-gray-600 dark:text-gray-400">
            Manage your personal information and account settings
          </p>
        </div>
      </div>

      <div className="space-y-6">
        {/* Profile Information */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <User className="h-5 w-5" />
              Personal Information
            </CardTitle>
            {!isEditing && (
              <Button 
                variant="outline" 
                size="sm"
                onClick={() => setIsEditing(true)}
              >
                <Edit className="h-4 w-4 mr-2" />
                Edit
              </Button>
            )}
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="firstName">First Name</Label>
                  {isEditing ? (
                    <Input
                      id="firstName"
                      value={formData.firstName}
                      onChange={(e) => setFormData(prev => ({ ...prev, firstName: e.target.value }))}
                      placeholder="Enter your first name"
                    />
                  ) : (
                    <p className="mt-1 px-3 py-2 text-gray-900 dark:text-gray-100 bg-gray-50 dark:bg-gray-800 rounded-md">
                      {user?.firstName || 'Not provided'}
                    </p>
                  )}
                </div>
                <div>
                  <Label htmlFor="lastName">Last Name</Label>
                  {isEditing ? (
                    <Input
                      id="lastName"
                      value={formData.lastName}
                      onChange={(e) => setFormData(prev => ({ ...prev, lastName: e.target.value }))}
                      placeholder="Enter your last name"
                    />
                  ) : (
                    <p className="mt-1 px-3 py-2 text-gray-900 dark:text-gray-100 bg-gray-50 dark:bg-gray-800 rounded-md">
                      {user?.lastName || 'Not provided'}
                    </p>
                  )}
                </div>
              </div>

              <div>
                <Label htmlFor="email">Email Address</Label>
                {isEditing ? (
                  <Input
                    id="email"
                    type="email"
                    value={formData.email}
                    onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
                    placeholder="Enter your email address"
                  />
                ) : (
                  <p className="mt-1 px-3 py-2 text-gray-900 dark:text-gray-100 bg-gray-50 dark:bg-gray-800 rounded-md flex items-center gap-2">
                    <Mail className="h-4 w-4" />
                    {user?.email || 'Not provided'}
                  </p>
                )}
              </div>

              <div>
                <Label>Username</Label>
                <p className="mt-1 px-3 py-2 text-gray-500 dark:text-gray-400 bg-gray-50 dark:bg-gray-800 rounded-md flex items-center gap-2">
                  <Lock className="h-4 w-4" />
                  {user?.username} (Cannot be changed)
                </p>
              </div>

              {isEditing && (
                <div className="flex gap-2 pt-4">
                  <Button 
                    type="submit" 
                    disabled={updateProfileMutation.isPending}
                  >
                    {updateProfileMutation.isPending ? (
                      <>
                        <div className="animate-spin rounded-full h-4 w-4 border-2 border-white border-t-transparent mr-2"></div>
                        Saving...
                      </>
                    ) : (
                      <>
                        <Save className="h-4 w-4 mr-2" />
                        Save Changes
                      </>
                    )}
                  </Button>
                  <Button 
                    type="button" 
                    variant="outline"
                    onClick={handleCancel}
                    disabled={updateProfileMutation.isPending}
                  >
                    Cancel
                  </Button>
                </div>
              )}
            </form>
          </CardContent>
        </Card>

        {/* Account Information */}
        <Card>
          <CardHeader>
            <CardTitle>Account Information</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label>Member Since</Label>
              <p className="mt-1 text-gray-900 dark:text-gray-100">
                {user?.createdAt 
                  ? new Date(user.createdAt).toLocaleDateString('en-US', {
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric'
                    })
                  : 'Not available'
                }
              </p>
            </div>
            <div>
              <Label>Account Type</Label>
              <p className="mt-1 text-gray-900 dark:text-gray-100">
                Customer Account
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/admin/CategoryMana.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertCategorySchema, type InsertCategory, type Category } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Plus, Edit, Trash2, Package, Upload } from "lucide-react";
import { createImageUploadHandler } from "@/lib/imageUpload";

export function CategoryManager() {
  const { toast } = useToast();
  const [isAddOpen, setIsAddOpen] = useState(false);
  const [editingCategory, setEditingCategory] = useState<Category | null>(null);
  const [isUploading, setIsUploading] = useState(false);

  const { data: categories, isLoading } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  const form = useForm<InsertCategory>({
    resolver: zodResolver(insertCategorySchema),
    defaultValues: {
      name: "",
      description: "",
      imageUrl: "",
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: InsertCategory) => {
      const response = await apiRequest("/api/categories", "POST", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      setIsAddOpen(false);
      form.reset();
      toast({
        title: "Success",
        description: "Category created successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create category",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: InsertCategory }) => {
      const response = await apiRequest(`/api/categories/${id}`, "PUT", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      setEditingCategory(null);
      form.reset();
      toast({
        title: "Success",
        description: "Category updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update category",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (categoryId: string) => {
      await apiRequest(`/api/categories/${categoryId}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      toast({
        title: "Success",
        description: "Category deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete category",
        variant: "destructive",
      });
    },
  });

  const handleImageUpload = createImageUploadHandler(
    (url) => form.setValue("imageUrl", url),
    setIsUploading
  );

  const onSubmit = (data: InsertCategory) => {
    console.log("Form submitted with data:", data);
    console.log("Form errors:", form.formState.errors);

    if (editingCategory) {
      updateMutation.mutate({ id: editingCategory.id, data });
    } else {
      createMutation.mutate(data);
    }
  };

  const handleEdit = (category: Category) => {
    setEditingCategory(category);
    form.reset({
      name: category.name,
      description: category.description || "",
      imageUrl: category.imageUrl || "",
    });
  };

  const handleCloseDialog = () => {
    setIsAddOpen(false);
    setEditingCategory(null);
    form.reset();
  };

  if (isLoading) {
    return <div>Loading categories...</div>;
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Package className="h-5 w-5" />
            Category Management
          </CardTitle>
          <Button onClick={() => {
            console.log("Add Category button clicked");
            setIsAddOpen(true);
          }} className="flex items-center gap-2">
            <Plus className="h-4 w-4" />
            Add Category
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        {/* Category Dialog */}
        <Dialog open={isAddOpen || !!editingCategory} onOpenChange={handleCloseDialog}>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>
                  {editingCategory ? "Edit Category" : "Add New Category"}
                </DialogTitle>
                <DialogDescription>
                  {editingCategory ? "Update the category information below." : "Fill in the details to create a new category."}
                </DialogDescription>
              </DialogHeader>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Name</FormLabel>
                        <FormControl>
                          <Input placeholder="Category name" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description</FormLabel>
                        <FormControl>
                          <Textarea placeholder="Category description" {...field} value={field.value || ""} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="imageUrl"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Image</FormLabel>
                        <div className="space-y-2">
                          <FormControl>
                            <Input placeholder="https://example.com/image.jpg" {...field} value={field.value || ""} />
                          </FormControl>
                          <div className="flex items-center gap-2">
                            <Button
                              type="button"
                              variant="outline"
                              size="sm"
                              onClick={() => document.getElementById('category-image-upload')?.click()}
                              disabled={isUploading}
                              className="flex items-center gap-2"
                            >
                              <Upload className="h-4 w-4" />
                              {isUploading ? "Uploading..." : "Upload Image"}
                            </Button>
                            <input
                              id="category-image-upload"
                              type="file"
                              accept="image/*"
                              className="hidden"
                              onChange={handleImageUpload}
                            />
                          </div>
                          {field.value && (
                            <img
                              src={field.value}
                              alt="Category preview"
                              className="w-20 h-20 object-cover rounded border"
                            />
                          )}
                        </div>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <div className="flex justify-end gap-2">
                    <Button type="button" variant="outline" onClick={handleCloseDialog}>
                      Cancel
                    </Button>
                    <Button 
                      type="submit" 
                      disabled={createMutation.isPending || updateMutation.isPending}
                    >
                      {editingCategory ? "Update" : "Create"}
                    </Button>
                  </div>
                </form>
              </Form>
            </DialogContent>
        </Dialog>
        <div className="grid gap-4">
          {categories?.length === 0 ? (
            <p className="text-center text-muted-foreground py-8">
              No categories found. Create your first category to get started.
            </p>
          ) : (
            categories?.map((category) => (
              <div
                key={category.id}
                className="flex items-center justify-between p-4 border rounded-lg"
              >
                <div className="flex-1">
                  <h3 className="font-semibold">{category.name}</h3>
                  {category.description && (
                    <p className="text-sm text-muted-foreground mt-1">
                      {category.description}
                    </p>
                  )}
                  {category.imageUrl && (
                    <p className="text-xs text-muted-foreground mt-1">
                      Image: {category.imageUrl}
                    </p>
                  )}
                </div>
                <div className="flex items-center gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handleEdit(category)}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => deleteMutation.mutate(category.id)}
                    disabled={deleteMutation.isPending}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            ))
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/admin/DataImportEx.content:
import { useState, useRef } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Download, Upload, FileSpreadsheet, Package, Users, ShoppingCart } from "lucide-react";
import * as XLSX from "xlsx";

type DataType = "products" | "categories" | "users" | "orders";

export function DataImportExport() {
  const { toast } = useToast();
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [selectedDataType, setSelectedDataType] = useState<DataType>("products");
  const [isImporting, setIsImporting] = useState(false);
  const [importProgress, setImportProgress] = useState(0);
  const [importResults, setImportResults] = useState<{
    success: number;
    errors: string[];
  } | null>(null);

  const { data: products } = useQuery({
    queryKey: ["/api/products"],
  });

  const { data: categories } = useQuery({
    queryKey: ["/api/categories"],
  });

  const { data: users } = useQuery({
    queryKey: ["/api/admin/users"],
  });

  const { data: orders } = useQuery({
    queryKey: ["/api/orders"],
  });

  const exportData = async (dataType: DataType) => {
    try {
      let data: any[] = [];
      let filename = "";

      switch (dataType) {
        case "products":
          data = Array.isArray(products) ? products : [];
          filename = "products_export.xlsx";
          break;
        case "categories":
          data = Array.isArray(categories) ? categories : [];
          filename = "categories_export.xlsx";
          break;
        case "users":
          data = Array.isArray(users) ? users : [];
          filename = "users_export.xlsx";
          break;
        case "orders":
          data = Array.isArray(orders) ? orders : [];
          filename = "orders_export.xlsx";
          break;
      }

      if (data.length === 0) {
        toast({
          title: "No Data",
          description: `No ${dataType} found to export`,
          variant: "destructive",
        });
        return;
      }

      // Create workbook and worksheet
      const wb = XLSX.utils.book_new();
      const ws = XLSX.utils.json_to_sheet(data);

      // Add worksheet to workbook
      XLSX.utils.book_append_sheet(wb, ws, dataType);

      // Generate Excel file and download
      XLSX.writeFile(wb, filename);

      toast({
        title: "Export Successful",
        description: `${data.length} ${dataType} exported successfully`,
      });
    } catch (error) {
      toast({
        title: "Export Failed",
        description: "Failed to export data",
        variant: "destructive",
      });
    }
  };

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsImporting(true);
    setImportProgress(0);
    setImportResults(null);

    try {
      const data = await file.arrayBuffer();
      const workbook = XLSX.read(data);
      const worksheet = workbook.Sheets[workbook.SheetNames[0]];
      const jsonData = XLSX.utils.sheet_to_json(worksheet);

      let successCount = 0;
      const errors: string[] = [];

      for (let i = 0; i < jsonData.length; i++) {
        try {
          const row = jsonData[i] as any;

          switch (selectedDataType) {
            case "products":
              await apiRequest("/api/products", "POST", {
                name: row.name || "",
                description: row.description || "",
                price: row.price || "0",
                imageUrl: row.imageUrl || "",
                categoryId: row.categoryId || "",

                sku: row.sku || "",
                isActive: row.isActive !== false,
                isFeatured: row.isFeatured === true,
                productType: row.productType || "sale",
                rentalPeriod: row.rentalPeriod || "",
                rentalPrice: row.rentalPrice || "",
              });
              break;
            case "categories":
              await apiRequest("/api/categories", "POST", {
                name: row.name || "",
                description: row.description || "",
                imageUrl: row.imageUrl || "",
              });
              break;
            case "users":
              await apiRequest("/api/admin/users", "POST", {
                username: row.username || "",
                email: row.email || "",
                password: row.password || "defaultPassword123",
                firstName: row.firstName || "",
                lastName: row.lastName || "",
                isAdmin: row.isAdmin === true,
              });
              break;
          }
          successCount++;
        } catch (error: any) {
          errors.push(`Row ${i + 2}: ${error.message || "Import failed"}`);
        }

        setImportProgress(((i + 1) / jsonData.length) * 100);
      }

      setImportResults({ success: successCount, errors });

      // Refresh data
      queryClient.invalidateQueries({ queryKey: [`/api/${selectedDataType}`] });
      if (selectedDataType === "users") {
        queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
      }

      toast({
        title: "Import Completed",
        description: `Successfully imported ${successCount} ${selectedDataType}`,
      });
    } catch (error) {
      toast({
        title: "Import Failed",
        description: "Failed to process the Excel file",
        variant: "destructive",
      });
    } finally {
      setIsImporting(false);
      if (fileInputRef.current) {
        fileInputRef.current.value = "";
      }
    }
  };

  const downloadTemplate = (dataType: DataType) => {
    let templateData: any[] = [];
    let filename = "";

    switch (dataType) {
      case "products":
        templateData = [
          {
            name: "Sample Product",
            description: "Sample product description",
            price: "29.99",
            imageUrl: "https://example.com/image.jpg",
            categoryId: "category-id-here",

            sku: "SAMPLE-001",
            isActive: true,
            isFeatured: false,
            productType: "sale",
            rentalPeriod: "",
            rentalPrice: "",
          },
        ];
        filename = "products_template.xlsx";
        break;
      case "categories":
        templateData = [
          {
            name: "Sample Category",
            description: "Sample category description",
            imageUrl: "https://example.com/image.jpg",
          },
        ];
        filename = "categories_template.xlsx";
        break;
      case "users":
        templateData = [
          {
            username: "sampleuser",
            email: "user@example.com",
            password: "defaultPassword123",
            firstName: "John",
            lastName: "Doe",
            isAdmin: false,
          },
        ];
        filename = "users_template.xlsx";
        break;
    }

    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.json_to_sheet(templateData);
    XLSX.utils.book_append_sheet(wb, ws, "Template");
    XLSX.writeFile(wb, filename);

    toast({
      title: "Template Downloaded",
      description: `${dataType} template downloaded successfully`,
    });
  };

  const getDataTypeIcon = (type: DataType) => {
    switch (type) {
      case "products":
        return <Package className="h-4 w-4" />;
      case "categories":
        return <ShoppingCart className="h-4 w-4" />;
      case "users":
        return <Users className="h-4 w-4" />;
      case "orders":
        return <FileSpreadsheet className="h-4 w-4" />;
    }
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <FileSpreadsheet className="h-5 w-5" />
          Data Import & Export
        </CardTitle>
      </CardHeader>
      <CardContent>
        <Tabs defaultValue="export">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="export">Export Data</TabsTrigger>
            <TabsTrigger value="import">Import Data</TabsTrigger>
          </TabsList>

          <TabsContent value="export" className="space-y-4">
            <div className="space-y-4">
              <div>
                <Label htmlFor="export-type">Select Data Type to Export</Label>
                <Select value={selectedDataType} onValueChange={(value: DataType) => setSelectedDataType(value)}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select data type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="products">
                      <div className="flex items-center gap-2">
                        <Package className="h-4 w-4" />
                        Products
                      </div>
                    </SelectItem>
                    <SelectItem value="categories">
                      <div className="flex items-center gap-2">
                        <ShoppingCart className="h-4 w-4" />
                        Categories
                      </div>
                    </SelectItem>
                    <SelectItem value="users">
                      <div className="flex items-center gap-2">
                        <Users className="h-4 w-4" />
                        Users
                      </div>
                    </SelectItem>
                    <SelectItem value="orders">
                      <div className="flex items-center gap-2">
                        <FileSpreadsheet className="h-4 w-4" />
                        Orders
                      </div>
                    </SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <Button
                onClick={() => exportData(selectedDataType)}
                className="flex items-center gap-2"
              >
                <Download className="h-4 w-4" />
                Export {selectedDataType}
              </Button>
            </div>
          </TabsContent>

          <TabsContent value="import" className="space-y-4">
            <div className="space-y-4">
              <div>
                <Label htmlFor="import-type">Select Data Type to Import</Label>
                <Select value={selectedDataType} onValueChange={(value: DataType) => setSelectedDataType(value)}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select data type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="products">
                      <div className="flex items-center gap-2">
                        <Package className="h-4 w-4" />
                        Products
                      </div>
                    </SelectItem>
                    <SelectItem value="categories">
                      <div className="flex items-center gap-2">
                        <ShoppingCart className="h-4 w-4" />
                        Categories
                      </div>
                    </SelectItem>
                    <SelectItem value="users">
                      <div className="flex items-center gap-2">
                        <Users className="h-4 w-4" />
                        Users
                      </div>
                    </SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="flex gap-2">
                <Button
                  variant="outline"
                  onClick={() => downloadTemplate(selectedDataType)}
                  className="flex items-center gap-2"
                >
                  <Download className="h-4 w-4" />
                  Download Template
                </Button>
                <Button
                  onClick={() => fileInputRef.current?.click()}
                  disabled={isImporting}
                  className="flex items-center gap-2"
                >
                  <Upload className="h-4 w-4" />
                  Upload Excel File
                </Button>
              </div>

              <Input
                ref={fileInputRef}
                type="file"
                accept=".xlsx,.xls"
                onChange={handleFileUpload}
                className="hidden"
              />

              {isImporting && (
                <div className="space-y-2">
                  <div className="flex items-center gap-2">
                    <span className="text-sm">Importing {selectedDataType}...</span>
                  </div>
                  <Progress value={importProgress} className="w-full" />
                </div>
              )}

              {importResults && (
                <Alert>
                  <AlertDescription>
                    <div className="space-y-2">
                      <p>Import completed: {importResults.success} items imported successfully</p>
                      {importResults.errors.length > 0 && (
                        <div>
                          <p className="font-semibold text-red-600">Errors:</p>
                          <ul className="text-sm text-red-600 list-disc list-inside">
                            {importResults.errors.slice(0, 5).map((error, index) => (
                              <li key={index}>{error}</li>
                            ))}
                            {importResults.errors.length > 5 && (
                              <li>... and {importResults.errors.length - 5} more errors</li>
                            )}
                          </ul>
                        </div>
                      )}
                    </div>
                  </AlertDescription>
                </Alert>
              )}
            </div>
          </TabsContent>
        </Tabs>
      </CardContent>
    </Card>
  );
}

client/src/components/admin/DatabaseMana.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Download, Upload, Database, AlertTriangle, FileDown, FileUp } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";

export default function DatabaseManager() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [importFile, setImportFile] = useState<File | null>(null);

  const exportMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch("/api/admin/database/export", {
        method: "GET",
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to export database");
      }

      return response.json();
    },
    onSuccess: (data) => {
      if (data.success && data.downloadUrl) {
        // Create a download link
        const link = document.createElement('a');
        link.href = data.downloadUrl;
        link.download = data.filename;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);

        toast({
          title: "Export Successful",
          description: "Database has been exported and downloaded successfully.",
        });
      }
    },
    onError: (error) => {
      toast({
        title: "Export Failed",
        description: error instanceof Error ? error.message : "Failed to export database",
        variant: "destructive",
      });
    },
  });

  const importMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append('database', file);

      const response = await fetch("/api/admin/database/import", {
        method: "POST",
        body: formData,
        credentials: "include",
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to import database");
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Import Successful",
        description: "Database has been imported successfully. The page will refresh to show updated data.",
      });

      // Invalidate all queries to refresh data
      queryClient.invalidateQueries();

      // Refresh the page after a short delay
      setTimeout(() => {
        window.location.reload();
      }, 2000);
    },
    onError: (error) => {
      toast({
        title: "Import Failed",
        description: error instanceof Error ? error.message : "Failed to import database",
        variant: "destructive",
      });
    },
  });

  const handleImport = () => {
    if (!importFile) {
      toast({
        title: "No File Selected",
        description: "Please select a database file to import.",
        variant: "destructive",
      });
      return;
    }

    if (!importFile.name.endsWith('.sql')) {
      toast({
        title: "Invalid File Type",
        description: "Please select a valid SQL database export file.",
        variant: "destructive",
      });
      return;
    }

    importMutation.mutate(importFile);
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center space-x-2">
        <Database className="h-5 w-5 text-blue-600" />
        <h2 className="text-2xl font-bold text-slate-900">Database Management</h2>
      </div>

      <Alert>
        <AlertTriangle className="h-4 w-4" />
        <AlertDescription>
          <strong>Important:</strong> Database operations affect your entire store data. 
          Always create a backup before importing data. Importing will replace existing products and categories.
        </AlertDescription>
      </Alert>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Export Database */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <FileDown className="h-5 w-5 text-green-600" />
              <span>Export Database</span>
            </CardTitle>
            <CardDescription>
              Download a complete backup of your store data including products, categories, orders, and settings.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="text-sm text-muted-foreground">
                <p>The SQL export will include:</p>
                <ul className="list-disc ml-4 mt-2 space-y-1">
                  <li>All products and categories with proper SQL INSERT statements</li>
                  <li>Site settings with UPDATE statements (passwords excluded)</li>
                  <li>Compatible with PostgreSQL database format</li>
                  <li>Timestamped backup with version information</li>
                </ul>
              </div>

              <Button 
                onClick={() => exportMutation.mutate()}
                disabled={exportMutation.isPending}
                className="w-full flex items-center space-x-2"
              >
                <Download className="h-4 w-4" />
                <span>{exportMutation.isPending ? "Exporting..." : "Export Database"}</span>
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Import Database */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <FileUp className="h-5 w-5 text-orange-600" />
              <span>Import Database</span>
            </CardTitle>
            <CardDescription>
              Restore your store data from a previous SQL export file. This will replace existing data.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="database-file">Select Database File</Label>
                <Input
                  id="database-file"
                  type="file"
                  accept=".sql"
                  onChange={(e) => setImportFile(e.target.files?.[0] || null)}
                  className="cursor-pointer"
                />
                <p className="text-xs text-muted-foreground">
                  Only SQL export files are accepted
                </p>
              </div>

              <div className="text-sm text-amber-700 bg-amber-50 p-3 rounded-lg border border-amber-200">
                <p className="font-medium">‚ö†Ô∏è Warning:</p>
                <p>Importing will replace your current products and categories. User accounts and sensitive data are preserved for security.</p>
              </div>

              <Button 
                onClick={handleImport}
                disabled={importMutation.isPending || !importFile}
                className="w-full flex items-center space-x-2"
                variant={importFile ? "default" : "secondary"}
              >
                <Upload className="h-4 w-4" />
                <span>{importMutation.isPending ? "Importing..." : "Import Database"}</span>
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Additional Information */}
      <Card>
        <CardHeader>
          <CardTitle>Best Practices</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
            <div>
              <h4 className="font-medium mb-2 text-green-700">‚úÖ Do:</h4>
              <ul className="space-y-1 text-muted-foreground">
                <li>‚Ä¢ Export regularly for backups</li>
                <li>‚Ä¢ Test imports on development first</li>
                <li>‚Ä¢ Verify file integrity before importing</li>
                <li>‚Ä¢ Keep multiple backup versions</li>
              </ul>
            </div>
            <div>
              <h4 className="font-medium mb-2 text-red-700">‚ùå Don't:</h4>
              <ul className="space-y-1 text-muted-foreground">
                <li>‚Ä¢ Import untrusted database files</li>
                <li>‚Ä¢ Skip backups before major changes</li>
                <li>‚Ä¢ Import during high traffic periods</li>
                <li>‚Ä¢ Edit export files manually</li>
              </ul>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/admin/ExcelManager.content:
import { useState, useRef } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { 
  Download, 
  Upload, 
  FileSpreadsheet, 
  AlertTriangle, 
  Package, 
  Users, 
  FolderOpen,
  Database
} from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { IndividualExcelManager } from "./IndividualExcelManager";

export default function ExcelManager() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [importFile, setImportFile] = useState<File | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const exportMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch("/api/admin/export/excel/bulk", {
        method: "GET",
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to export Excel file");
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const filename = `data-export-${new Date().toISOString().split('T')[0]}.xlsx`;

      // Create download link
      const link = document.createElement('a');
      link.href = url;
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      return { success: true };
    },
    onSuccess: () => {
      toast({
        title: "Export Successful",
        description: "Excel file has been exported and downloaded successfully.",
      });
    },
    onError: (error) => {
      toast({
        title: "Export Failed",
        description: error instanceof Error ? error.message : "Failed to export Excel file",
        variant: "destructive",
      });
    },
  });

  const importMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append('excel', file);

      const response = await fetch("/api/admin/import/excel", {
        method: "POST",
        credentials: "include",
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to import Excel file");
      }

      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Import Successful",
        description: data.message || "Excel file has been imported successfully.",
      });
      setImportFile(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }

      // Invalidate relevant queries to refresh data
      queryClient.invalidateQueries({ queryKey: ['/api/products'] });
      queryClient.invalidateQueries({ queryKey: ['/api/categories'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/users'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/stats'] });
    },
    onError: (error) => {
      toast({
        title: "Import Failed",
        description: error instanceof Error ? error.message : "Failed to import Excel file",
        variant: "destructive",
      });
    },
  });

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    setImportFile(file || null);
  };

  const handleImport = () => {
    if (!importFile) {
      toast({
        title: "No File Selected",
        description: "Please select an Excel file to import.",
        variant: "destructive",
      });
      return;
    }

    if (!importFile.name.toLowerCase().endsWith('.xlsx') && !importFile.name.toLowerCase().endsWith('.xls')) {
      toast({
        title: "Invalid File Type",
        description: "Please select a valid Excel file (.xlsx or .xls).",
        variant: "destructive",
      });
      return;
    }

    importMutation.mutate(importFile);
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center space-x-2">
        <FileSpreadsheet className="h-5 w-5 text-green-600" />
        <h2 className="text-2xl font-bold text-slate-900">Excel Data Management</h2>
      </div>

      <Alert>
        <AlertTriangle className="h-4 w-4" />
        <AlertDescription>
          <strong>Comprehensive Excel Data Management:</strong> Export all your e-commerce data to Excel for analysis or import bulk data from Excel files. 
          The Excel workbook contains 8 separate sheets covering all data types: Products (with units of measure), Categories, Users, Orders, Order Items, Units of Measure, Site Settings, and Slider Images. Perfect for data backups, analysis, and bulk operations.
        </AlertDescription>
      </Alert>

      <Tabs defaultValue="individual" className="w-full">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="individual" className="flex items-center space-x-2">
            <FileSpreadsheet className="h-4 w-4" />
            <span>Individual Sheets</span>
          </TabsTrigger>
          <TabsTrigger value="bulk" className="flex items-center space-x-2">
            <Database className="h-4 w-4" />
            <span>Bulk Operations</span>
          </TabsTrigger>
        </TabsList>

        <TabsContent value="individual" className="space-y-6">
          <IndividualExcelManager />
        </TabsContent>

        <TabsContent value="bulk" className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Export Excel */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Download className="h-5 w-5 text-green-600" />
              <span>Export to Excel</span>
            </CardTitle>
            <CardDescription>
              Download all your store data in a comprehensive Excel workbook format.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="text-sm text-muted-foreground">
                <p>The Excel export includes 8 comprehensive data sheets:</p>
                <ul className="list-disc ml-4 mt-2 space-y-1">
                  <li className="flex items-center gap-2">
                    <Package className="h-3 w-3" />
                    <strong>Products:</strong> All product data including prices, units of measure, categories
                  </li>
                  <li className="flex items-center gap-2">
                    <FolderOpen className="h-3 w-3" />
                    <strong>Categories:</strong> All category information and descriptions
                  </li>
                  <li className="flex items-center gap-2">
                    <Users className="h-3 w-3" />
                    <strong>Users:</strong> User accounts (passwords excluded for security)
                  </li>
                  <li className="flex items-center gap-2">
                    <Package className="h-3 w-3" />
                    <strong>Units of Measure:</strong> All measurement units (kg, liter, piece, etc.)
                  </li>
                  <li className="flex items-center gap-2">
                    <Package className="h-3 w-3" />
                    <strong>Orders:</strong> Complete order history and status information
                  </li>
                  <li className="flex items-center gap-2">
                    <Package className="h-3 w-3" />
                    <strong>Order Items:</strong> Individual items within each order
                  </li>
                  <li className="flex items-center gap-2">
                    <Package className="h-3 w-3" />
                    <strong>Site Settings:</strong> All website configuration and customization settings
                  </li>
                  <li className="flex items-center gap-2">
                    <Package className="h-3 w-3" />
                    <strong>Slider Images:</strong> Homepage slider configuration and image data
                  </li>
                </ul>
              </div>

              <Button 
                onClick={() => exportMutation.mutate()}
                disabled={exportMutation.isPending}
                className="w-full flex items-center space-x-2"
              >
                <Download className="h-4 w-4" />
                <span>{exportMutation.isPending ? "Exporting..." : "Export to Excel"}</span>
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Import Excel */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Upload className="h-5 w-5 text-blue-600" />
              <span>Import from Excel</span>
            </CardTitle>
            <CardDescription>
              Bulk import products, categories, and users from an Excel file. This will replace existing data.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="excel-file">Select Excel File</Label>
                <Input
                  id="excel-file"
                  ref={fileInputRef}
                  type="file"
                  accept=".xlsx,.xls"
                  onChange={handleFileChange}
                  className="cursor-pointer"
                />
                <p className="text-xs text-muted-foreground">
                  Only Excel files (.xlsx, .xls) are accepted
                </p>
              </div>

              <div className="text-sm text-muted-foreground">
                <p><strong>Excel file structure expected (8 sheets):</strong></p>
                <ul className="list-disc ml-4 mt-1 space-y-1 text-xs">
                  <li>Sheet 1: "Products" - Name, Description, Price, Stock, SKU, Unit of Measure, Category ID, etc.</li>
                  <li>Sheet 2: "Categories" - Name, Description, Image URL</li>
                  <li>Sheet 3: "Users" - Username, Email, First Name, Last Name, Is Admin</li>
                  <li>Sheet 4: "Units of Measure" - Name, Abbreviation, Is Active</li>
                  <li>Sheet 5: "Orders" - User ID, Status, Total Amount, Shipping Address, Payment Method</li>
                  <li>Sheet 6: "Order Items" - Order ID, Product ID, Quantity, Price</li>
                  <li>Sheet 7: "Site Settings" - All website configuration settings</li>
                  <li>Sheet 8: "Slider Images" - Title, Image URL, Link URL, Is Active, Display Order</li>
                </ul>
              </div>

              <div className="p-3 bg-green-50 rounded-lg border border-green-200">
                <p className="text-sm font-medium text-green-900">‚úì Flexible Import:</p>
                <p className="text-xs text-green-700">Excel sheets can have any combination of fields. Missing columns will use default values, and missing IDs will be auto-generated by the database.</p>
              </div>

              <div className="p-3 bg-amber-50 rounded-lg border border-amber-200">
                <p className="text-sm font-medium text-amber-900">‚ö†Ô∏è Important Notes:</p>
                <ul className="text-xs text-amber-700 mt-1 space-y-1">
                  <li>‚Ä¢ Import will replace existing data in each sheet</li>
                  <li>‚Ä¢ User passwords will not be imported for security</li>
                  <li>‚Ä¢ New users get random passwords that need reset</li>
                </ul>
              </div>

              {importFile && (
                <div className="p-3 bg-blue-50 rounded-lg border border-blue-200">
                  <p className="text-sm font-medium text-blue-900">Selected File:</p>
                  <p className="text-sm text-blue-700">{importFile.name}</p>
                  <p className="text-xs text-blue-600">Size: {(importFile.size / 1024 / 1024).toFixed(2)} MB</p>
                </div>
              )}

              <Button 
                onClick={handleImport}
                disabled={!importFile || importMutation.isPending}
                className="w-full flex items-center space-x-2"
              >
                <Upload className="h-4 w-4" />
                <span>{importMutation.isPending ? "Importing..." : "Import Excel Data"}</span>
              </Button>

              {importMutation.isPending && (
                <div className="text-sm text-muted-foreground">
                  <p>Processing Excel file and importing data...</p>
                  <p className="text-xs">This may take a few moments for large files.</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}

