import { useState, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Download, Upload, FileSpreadsheet, Database, Users, Package, ShoppingCart, Settings, Image, Ruler } from "lucide-react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";

export function IndividualExcelManager() {
  const [importFiles, setImportFiles] = useState<{ [key: string]: File | null }>({});
  const fileInputRefs = useRef<{ [key: string]: HTMLInputElement | null }>({});
  const { toast } = useToast();

  const sheetTypes = [
    { 
      id: 'products', 
      name: 'Products', 
      icon: Package,
      description: 'Product catalog with prices and details',
      fields: 'Name, Description, Price, SKU, Unit of Measure, Category ID, Image URL, etc.'
    },
    { 
      id: 'categories', 
      name: 'Categories', 
      icon: Database,
      description: 'Product categories and organizational structure',
      fields: 'Name, Description, Image URL'
    },
    { 
      id: 'users', 
      name: 'Users', 
      icon: Users,
      description: 'User accounts and customer information',
      fields: 'Username, Email, First Name, Last Name, Is Admin'
    },
    { 
      id: 'orders', 
      name: 'Orders', 
      icon: ShoppingCart,
      description: 'Customer orders and transaction history',
      fields: 'User ID, Status, Total Amount, Shipping Address, Payment Method'
    },
    { 
      id: 'order-items', 
      name: 'Order Items', 
      icon: FileSpreadsheet,
      description: 'Individual items within each order',
      fields: 'Order ID, Product ID, Quantity, Price'
    },
    { 
      id: 'units', 
      name: 'Units of Measure', 
      icon: Ruler,
      description: 'Measurement units for products',
      fields: 'Name, Abbreviation, Is Active'
    },
    { 
      id: 'site-settings', 
      name: 'Site Settings', 
      icon: Settings,
      description: 'Website configuration and preferences',
      fields: 'Site Name, Logos, Footer Content, Social Links'
    },
    { 
      id: 'slider-images', 
      name: 'Slider Images', 
      icon: Image,
      description: 'Homepage slider and promotional images',
      fields: 'Title, Image URL, Link URL, Is Active, Display Order'
    }
  ];

  const exportMutation = useMutation({
    mutationFn: async (sheetType: string) => {
      const response = await fetch(`/api/admin/export/excel/${sheetType}`, {
        method: 'GET',
        credentials: 'include'
      });

      if (!response.ok) {
        throw new Error(`Failed to export ${sheetType}`);
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      a.download = `${sheetType}-export-${new Date().toISOString().split('T')[0]}.xlsx`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    },
    onSuccess: (_, sheetType) => {
      toast({
        title: "Export Successful",
        description: `${sheetType} data exported successfully`,
      });
    },
    onError: (error: any, sheetType) => {
      console.error('Export error:', error);
      toast({
        title: "Export Failed",
        description: `Failed to export ${sheetType}: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  const importMutation = useMutation({
    mutationFn: async ({ sheetType, file }: { sheetType: string; file: File }) => {
      const formData = new FormData();
      formData.append('excel', file);

      const response = await fetch(`/api/admin/import/excel/${sheetType}`, {
        method: 'POST',
        credentials: 'include',
        body: formData
      });

      if (!response.ok) {
        const error = await response.text();
        throw new Error(error || 'Import failed');
      }

      return response.json();
    },
    onSuccess: (data, { sheetType }) => {
      toast({
        title: "Import Successful",
        description: data.message || `${sheetType} data imported successfully`,
      });

      // Clear the file input
      setImportFiles(prev => ({ ...prev, [sheetType]: null }));
      if (fileInputRefs.current[sheetType]) {
        fileInputRefs.current[sheetType]!.value = '';
      }

      // Invalidate relevant caches to refresh data in admin sections
      if (sheetType === 'categories') {
        queryClient.invalidateQueries({ queryKey: ['/api/categories'] });
        queryClient.invalidateQueries({ queryKey: ['/api/admin/categories'] });
      } else if (sheetType === 'products') {
        queryClient.invalidateQueries({ queryKey: ['/api/products'] });
        queryClient.invalidateQueries({ queryKey: ['/api/admin/products'] });
        queryClient.invalidateQueries({ queryKey: ['/api/products/featured'] });
      } else if (sheetType === 'users') {
        queryClient.invalidateQueries({ queryKey: ['/api/admin/users'] });
      } else if (sheetType === 'orders') {
        queryClient.invalidateQueries({ queryKey: ['/api/admin/orders'] });
        queryClient.invalidateQueries({ queryKey: ['/api/admin/stats'] });
      } else if (sheetType === 'order-items') {
        queryClient.invalidateQueries({ queryKey: ['/api/admin/orders'] });
        queryClient.invalidateQueries({ queryKey: ['/api/admin/stats'] });
      } else if (sheetType === 'units') {
        queryClient.invalidateQueries({ queryKey: ['/api/units-of-measure'] });
      } else if (sheetType === 'site-settings') {
        queryClient.invalidateQueries({ queryKey: ['/api/settings'] });
      } else if (sheetType === 'slider-images') {
        queryClient.invalidateQueries({ queryKey: ['/api/slider-images'] });
        queryClient.invalidateQueries({ queryKey: ['/api/slider-images/active'] });
      }
    },
    onError: (error: any, { sheetType }) => {
      console.error('Import error:', error);
      toast({
        title: "Import Failed",
        description: `Failed to import ${sheetType}: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  const handleFileChange = (sheetType: string, event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    setImportFiles(prev => ({ ...prev, [sheetType]: file || null }));
  };

  const handleExport = (sheetType: string) => {
    exportMutation.mutate(sheetType);
  };

  const handleImport = (sheetType: string) => {
    const file = importFiles[sheetType];
    if (!file) {
      toast({
        title: "No File Selected",
        description: "Please select an Excel file to import",
        variant: "destructive",
      });
      return;
    }
    importMutation.mutate({ sheetType, file });
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center space-x-2">
        <FileSpreadsheet className="h-6 w-6 text-green-600" />
        <h2 className="text-2xl font-bold">Individual Excel Management</h2>
      </div>

      <div className="p-4 bg-blue-50 rounded-lg border border-blue-200">
        <h3 className="font-medium text-blue-900 mb-2">‚úì Individual Sheet Control</h3>
        <p className="text-sm text-blue-700">Export and import each data type independently to avoid foreign key conflicts and gain precise control over your data management.</p>
      </div>

      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
        {sheetTypes.map((sheet) => {
          const Icon = sheet.icon;
          const isExporting = exportMutation.isPending;
          const isImporting = importMutation.isPending;
          const selectedFile = importFiles[sheet.id];

          return (
            <Card key={sheet.id} className="flex flex-col">
              <CardHeader className="pb-3">
                <CardTitle className="flex items-center space-x-2 text-lg">
                  <Icon className="h-5 w-5 text-blue-600" />
                  <span>{sheet.name}</span>
                </CardTitle>
                <CardDescription className="text-sm">
                  {sheet.description}
                </CardDescription>
              </CardHeader>

              <CardContent className="flex-1 space-y-4">
                <div className="text-xs text-muted-foreground">
                  <strong>Fields:</strong> {sheet.fields}
                </div>

                {/* Export Section */}
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Export</Label>
                  <Button
                    onClick={() => handleExport(sheet.id)}
                    disabled={isExporting}
                    size="sm"
                    className="w-full flex items-center space-x-2"
                  >
                    <Download className="h-4 w-4" />
                    <span>{isExporting ? "Exporting..." : "Export to Excel"}</span>
                  </Button>
                </div>

                {/* Import Section */}
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Import</Label>
                  <Input
                    ref={(el) => fileInputRefs.current[sheet.id] = el}
                    type="file"
                    accept=".xlsx,.xls"
                    onChange={(e) => handleFileChange(sheet.id, e)}
                    className="text-xs"
                  />
                  {selectedFile && (
                    <div className="p-2 bg-green-50 rounded text-xs">
                      <span className="font-medium text-green-900">Selected:</span>
                      <span className="text-green-700 ml-1">{selectedFile.name}</span>
                    </div>
                  )}
                  <Button
                    onClick={() => handleImport(sheet.id)}
                    disabled={!selectedFile || isImporting}
                    size="sm"
                    variant="outline"
                    className="w-full flex items-center space-x-2"
                  >
                    <Upload className="h-4 w-4" />
                    <span>{isImporting ? "Importing..." : "Import from Excel"}</span>
                  </Button>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      <div className="p-4 bg-amber-50 rounded-lg border border-amber-200">
        <h3 className="font-medium text-amber-900 mb-2">üí° Tips for Individual Management</h3>
        <ul className="text-sm text-amber-700 space-y-1">
          <li>‚Ä¢ Export individual sheets to get exact field structures</li>
          <li>‚Ä¢ Import categories and units first, then products that reference them</li>
          <li>‚Ä¢ Import users before orders, and orders before order items</li>
          <li>‚Ä¢ Missing columns will use default values automatically</li>
          <li>‚Ä¢ Empty or missing IDs will be auto-generated by the database</li>
        </ul>
      </div>
    </div>
  );
}

client/src/components/admin/OrderManager.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  Dialog, 
  DialogContent, 
  DialogDescription, 
  DialogHeader, 
  DialogTitle, 
  DialogTrigger 
} from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { 
  CheckCircle, 
  XCircle, 
  Clock, 
  Eye, 
  Package, 
  DollarSign,
  Calendar,
  User,
  MapPin
} from "lucide-react";

interface Order {
  id: string;
  userId: string;
  status: string;
  adminApprovalStatus: string;
  total: string;
  subtotal: string;
  tax: string;
  vatPercentage: string;
  paymentMethod: string;
  shippingAddress: any;
  adminRemarks?: string;
  adminApprovedBy?: string;
  adminApprovedAt?: string;
  estimatedDeliveryDays: number;
  createdAt: string;
  user?: {
    name: string;
    email: string;
  };
  items?: Array<{
    id: string;
    quantity: number;
    price: string;
    product: {
      name: string;
      image?: string;
    };
  }>;
}

export function OrderManager() {
  const { toast } = useToast();
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [approvalAction, setApprovalAction] = useState<'approve' | 'reject' | null>(null);
  const [adminRemarks, setAdminRemarks] = useState("");

  const { data: orders, isLoading } = useQuery<Order[]>({
    queryKey: ["/api/admin/orders"],
  });

  const approveOrderMutation = useMutation({
    mutationFn: async ({ orderId, remarks }: { orderId: string; remarks: string }) => {
      const res = await apiRequest(`/api/admin/orders/${orderId}/approve`, "PUT", {
        adminRemarks: remarks
      });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/orders"] });
      toast({
        title: "Order Approved",
        description: "Customer has been notified via email",
      });
      setApprovalAction(null);
      setAdminRemarks("");
      setSelectedOrder(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to approve order",
        variant: "destructive",
      });
    },
  });

  const rejectOrderMutation = useMutation({
    mutationFn: async ({ orderId, remarks }: { orderId: string; remarks: string }) => {
      const res = await apiRequest(`/api/admin/orders/${orderId}/reject`, "PUT", {
        adminRemarks: remarks
      });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/orders"] });
      toast({
        title: "Order Rejected",
        description: "Customer has been notified via email",
      });
      setApprovalAction(null);
      setAdminRemarks("");
      setSelectedOrder(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to reject order",
        variant: "destructive",
      });
    },
  });

  const completeOrderMutation = useMutation({
    mutationFn: async (orderId: string) => {
      const res = await apiRequest(`/api/admin/orders/${orderId}/complete`, "PUT");
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/orders"] });
      toast({
        title: "Order Completed",
        description: "Customer has been notified of delivery",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to complete order",
        variant: "destructive",
      });
    },
  });

  const getStatusBadge = (status: string, approvalStatus: string) => {
    if (approvalStatus === "pending") {
      return <Badge variant="secondary"><Clock className="w-3 h-3 mr-1" />Awaiting Approval</Badge>;
    }
    if (approvalStatus === "approved") {
      return <Badge variant="default"><CheckCircle className="w-3 h-3 mr-1" />Approved</Badge>;
    }
    if (approvalStatus === "rejected") {
      return <Badge variant="destructive"><XCircle className="w-3 h-3 mr-1" />Rejected</Badge>;
    }

    switch (status) {
      case "payment_pending":
        return <Badge variant="outline"><DollarSign className="w-3 h-3 mr-1" />Payment Pending</Badge>;
      case "processing":
        return <Badge variant="secondary"><Package className="w-3 h-3 mr-1" />Processing</Badge>;
      case "shipped":
        return <Badge variant="default"><Package className="w-3 h-3 mr-1" />Shipped</Badge>;
      case "delivered":
        return <Badge variant="default"><CheckCircle className="w-3 h-3 mr-1" />Delivered</Badge>;
      case "cancelled":
        return <Badge variant="destructive"><XCircle className="w-3 h-3 mr-1" />Cancelled</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const handleApprovalAction = () => {
    if (!selectedOrder || !approvalAction) return;

    if (approvalAction === 'approve') {
      approveOrderMutation.mutate({
        orderId: selectedOrder.id,
        remarks: adminRemarks
      });
    } else {
      rejectOrderMutation.mutate({
        orderId: selectedOrder.id,
        remarks: adminRemarks
      });
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-4">
        <div className="h-6 bg-gray-200 rounded w-32 animate-pulse"></div>
        <div className="grid gap-4">
          {[1, 2, 3].map((i) => (
            <div key={i} className="h-32 bg-gray-200 rounded animate-pulse"></div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold text-gray-900">Order Management</h2>
        <div className="text-sm text-gray-500">
          {orders?.length || 0} total orders
        </div>
      </div>

      <div className="grid gap-4">
        {orders?.map((order) => (
          <Card key={order.id} className="hover:shadow-md transition-shadow">
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <CardTitle className="text-lg">Order #{order.id.slice(-8)}</CardTitle>
                  {getStatusBadge(order.status, order.adminApprovalStatus)}
                </div>
                <div className="flex items-center space-x-2">
                  <Dialog>
                    <DialogTrigger asChild>
                      <Button variant="outline" size="sm">
                        <Eye className="w-4 h-4 mr-1" />
                        View Details
                      </Button>
                    </DialogTrigger>
                    <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
                      <DialogHeader>
                        <DialogTitle>Order Details - #{order.id.slice(-8)}</DialogTitle>
                        <DialogDescription>
                          Complete order information and management actions
                        </DialogDescription>
                      </DialogHeader>

                      <div className="space-y-6">
                        {/* Customer Information */}
                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <h4 className="font-medium text-gray-900 mb-2 flex items-center">
                              <User className="w-4 h-4 mr-2" />
                              Customer Information
                            </h4>
                            <div className="space-y-1 text-sm text-gray-600">
                              <p><strong>Name:</strong> {order.user?.name}</p>
                              <p><strong>Email:</strong> {order.user?.email}</p>
                            </div>
                          </div>

                          <div>
                            <h4 className="font-medium text-gray-900 mb-2 flex items-center">
                              <Calendar className="w-4 h-4 mr-2" />
                              Order Information
                            </h4>
                            <div className="space-y-1 text-sm text-gray-600">
                              <p><strong>Date:</strong> {new Date(order.createdAt).toLocaleDateString()}</p>
                              <p><strong>Delivery:</strong> {order.estimatedDeliveryDays} days</p>
                              <p><strong>Payment:</strong> {order.paymentMethod}</p>
                            </div>
                          </div>
                        </div>



                        {/* Order Items */}
                        <div>
                          <h4 className="font-medium text-gray-900 mb-2">Order Items</h4>
                          <div className="space-y-2">
                            {order.items?.map((item) => (
                              <div key={item.id} className="flex justify-between items-center bg-gray-50 p-2 rounded">
                                <div>
                                  <p className="font-medium">{item.product.name}</p>
                                  <p className="text-sm text-gray-600">Quantity: {item.quantity}</p>
                                </div>
                                <div className="text-right">
                                  <p className="font-medium">${item.price}</p>
                                </div>
                              </div>
                            ))}
                          </div>
                        </div>

                        {/* Order Totals */}
                        <div className="bg-gray-50 p-4 rounded">
                          <div className="space-y-2">
                            <div className="flex justify-between">
                              <span>Subtotal:</span>
                              <span>${order.subtotal}</span>
                            </div>
                            <div className="flex justify-between">
                              <span>VAT ({order.vatPercentage}%):</span>
                              <span>${order.tax}</span>
                            </div>
                            <div className="flex justify-between font-bold text-lg border-t pt-2">
                              <span>Total:</span>
                              <span>${order.total}</span>
                            </div>
                          </div>
                        </div>

                        {/* Admin Remarks */}
                        {order.adminRemarks && (
                          <div>
                            <h4 className="font-medium text-gray-900 mb-2">Admin Remarks</h4>
                            <div className="bg-blue-50 p-3 rounded text-sm">
                              {order.adminRemarks}
                            </div>
                          </div>
                        )}
                      </div>
                    </DialogContent>
                  </Dialog>

                  {order.adminApprovalStatus === "pending" && (
                    <>
                      <Button
                        variant="default"
                        size="sm"
                        onClick={() => {
                          setSelectedOrder(order);
                          setApprovalAction('approve');
                        }}
                      >
                        <CheckCircle className="w-4 h-4 mr-1" />
                        Approve
                      </Button>
                      <Button
                        variant="destructive"
                        size="sm"
                        onClick={() => {
                          setSelectedOrder(order);
                          setApprovalAction('reject');
                        }}
                      >
                        <XCircle className="w-4 h-4 mr-1" />
                        Reject
                      </Button>
                    </>
                  )}

                  {order.status === "payment_pending" && (
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => completeOrderMutation.mutate(order.id)}
                    >
                      <Package className="w-4 h-4 mr-1" />
                      Mark Delivered
                    </Button>
                  )}
                </div>
              </div>
            </CardHeader>

            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                <div>
                  <p className="text-gray-600">Customer</p>
                  <p className="font-medium">{order.user?.name}</p>
                </div>
                <div>
                  <p className="text-gray-600">Total Amount</p>
                  <p className="font-medium">${order.total} (incl. {order.vatPercentage}% VAT)</p>
                </div>
                <div>
                  <p className="text-gray-600">Order Date</p>
                  <p className="font-medium">{new Date(order.createdAt).toLocaleDateString()}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}

        {orders?.length === 0 && (
          <Card className="text-center py-12">
            <CardContent>
              <Package className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No Orders Found</h3>
              <p className="text-gray-600">Orders will appear here once customers start placing them.</p>
            </CardContent>
          </Card>
        )}
      </div>

      {/* Approval Action Dialog */}
      <Dialog open={!!approvalAction} onOpenChange={() => {
        setApprovalAction(null);
        setAdminRemarks("");
        setSelectedOrder(null);
      }}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>
              {approvalAction === 'approve' ? 'Approve Order' : 'Reject Order'}
            </DialogTitle>
            <DialogDescription>
              {approvalAction === 'approve' 
                ? 'Approve this order and notify the customer to proceed with payment.'
                : 'Reject this order and notify the customer with a reason.'
              }
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            <div>
              <Label htmlFor="remarks">
                {approvalAction === 'approve' ? 'Additional Notes (Optional)' : 'Reason for Rejection (Required)'}
              </Label>
              <Textarea
                id="remarks"
                placeholder={approvalAction === 'approve' 
                  ? "Add any additional notes for the customer..." 
                  : "Please provide a reason for rejecting this order..."
                }
                value={adminRemarks}
                onChange={(e) => setAdminRemarks(e.target.value)}
                className="mt-1"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <Button
                variant="outline"
                onClick={() => {
                  setApprovalAction(null);
                  setAdminRemarks("");
                  setSelectedOrder(null);
                }}
              >
                Cancel
              </Button>
              <Button
                variant={approvalAction === 'approve' ? 'default' : 'destructive'}
                onClick={handleApprovalAction}
                disabled={approvalAction === 'reject' && !adminRemarks.trim()}
              >
                {approvalAction === 'approve' ? 'Approve Order' : 'Reject Order'}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

