import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Checkbox } from "@/components/ui/checkbox";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertProductSchema, type InsertProduct, type Product, type Category, type UnitOfMeasure } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Plus, Edit, Trash2, ShoppingCart, Package, Upload } from "lucide-react";
import { z } from "zod";
import { createImageUploadHandler } from "@/lib/imageUpload";

// Enhanced product schema with product type and proper number validation
const enhancedProductSchema = insertProductSchema.extend({
  productType: z.enum(["sale", "rental"]).default("sale"),
  rentalPeriod: z.string().optional(),
  rentalPrice: z.string().optional(),
}).transform((data) => ({
  ...data,
  // Convert string prices to numbers, handling empty strings
  price: data.price === "" ? "0" : data.price,
  rentalPrice: data.rentalPrice === "" ? undefined : data.rentalPrice,
}));

type EnhancedInsertProduct = z.infer<typeof enhancedProductSchema>;

export function ProductManager() {
  const { toast } = useToast();
  const [isAddOpen, setIsAddOpen] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [isUploading, setIsUploading] = useState(false);

  const { data: products, isLoading: productsLoading } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  const { data: categories } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  const { data: unitsOfMeasure } = useQuery<UnitOfMeasure[]>({
    queryKey: ["/api/units-of-measure"],
  });

  const form = useForm<EnhancedInsertProduct>({
    resolver: zodResolver(enhancedProductSchema),
    defaultValues: {
      name: "",
      description: "",
      price: "0",
      imageUrl: "",
      categoryId: "",
      sku: "",
      unitOfMeasure: "piece",
      isActive: true,
      isFeatured: false,
      productType: "sale",
      rentalPeriod: "",
      rentalPrice: "",
    },
  });

  const productType = form.watch("productType");

  const handleImageUpload = createImageUploadHandler(
    (url) => form.setValue("imageUrl", url),
    setIsUploading
  );

  const createMutation = useMutation({
    mutationFn: async (data: EnhancedInsertProduct) => {
      // Clean the data before sending to server
      const cleanData = {
        ...data,
        price: data.price || "0",
        rentalPrice: data.rentalPrice || undefined,
      };
      const response = await apiRequest("/api/products", "POST", cleanData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      setIsAddOpen(false);
      form.reset();
      toast({
        title: "Success",
        description: "Product created successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create product",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: EnhancedInsertProduct }) => {
      // Clean the data before sending to server
      const cleanData = {
        ...data,
        price: data.price || "0",
        rentalPrice: data.rentalPrice || undefined,
      };
      const response = await apiRequest(`/api/products/${id}`, "PUT", cleanData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      setEditingProduct(null);
      form.reset();
      toast({
        title: "Success",
        description: "Product updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update product",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (productId: string) => {
      await apiRequest(`/api/products/${productId}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({
        title: "Success",
        description: "Product deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete product",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: EnhancedInsertProduct) => {
    console.log("Product form submitted with data:", data);
    console.log("Product form errors:", form.formState.errors);

    if (editingProduct) {
      updateMutation.mutate({ id: editingProduct.id, data });
    } else {
      createMutation.mutate(data);
    }
  };

  const handleEdit = (product: Product) => {
    setEditingProduct(product);
    form.reset({
      name: product.name,
      description: product.description || "",
      price: product.price,
      imageUrl: product.imageUrl || "",
      categoryId: product.categoryId || "",
      sku: product.sku || "",
      unitOfMeasure: product.unitOfMeasure || "piece",
      isActive: product.isActive,
      isFeatured: product.isFeatured,
      productType: (product as any).productType || "sale",
      rentalPeriod: (product as any).rentalPeriod || "",
      rentalPrice: (product as any).rentalPrice || "",
    });
  };

  const handleCloseDialog = () => {
    setIsAddOpen(false);
    setEditingProduct(null);
    form.reset();
  };

  if (productsLoading) {
    return <div>Loading products...</div>;
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <ShoppingCart className="h-5 w-5" />
            Product Management
          </CardTitle>
          <Button onClick={() => {
            console.log("Add Product button clicked");
            setIsAddOpen(true);
          }} className="flex items-center gap-2">
            <Plus className="h-4 w-4" />
            Add Product
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        {/* Product Dialog */}
        <Dialog open={isAddOpen || !!editingProduct} onOpenChange={handleCloseDialog}>
            <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>
                  {editingProduct ? "Edit Product" : "Add New Product"}
                </DialogTitle>
                <DialogDescription>
                  {editingProduct ? "Update the product information below." : "Fill in the details to create a new product."}
                </DialogDescription>  
              </DialogHeader>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="name"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Product Name</FormLabel>
                          <FormControl>
                            <Input placeholder="Product name" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="sku"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>SKU</FormLabel>
                          <FormControl>
                            <Input placeholder="Product SKU" {...field} value={field.value || ""} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description</FormLabel>
                        <FormControl>
                          <Textarea placeholder="Product description" {...field} value={field.value || ""} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="productType"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Product Type</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select product type" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="sale">Sale</SelectItem>
                              <SelectItem value="rental">Rental</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="categoryId"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Category</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value || ""}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select category" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {categories?.map((category) => (
                                <SelectItem key={category.id} value={category.id}>
                                  {category.name}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="price"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Price</FormLabel>
                          <FormControl>
                            <Input type="number" step="0.01" placeholder="0.00" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="unitOfMeasure"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Unit of Measure</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value || "piece"}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select unit" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {unitsOfMeasure?.map((unit) => (
                                <SelectItem key={unit.id} value={unit.name}>
                                  {unit.name} ({unit.abbreviation})
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  {productType === "rental" && (
                    <div className="grid grid-cols-2 gap-4">
                      <FormField
                        control={form.control}
                        name="rentalPeriod"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Rental Period</FormLabel>
                            <Select onValueChange={field.onChange} value={field.value || ""}>
                              <FormControl>
                                <SelectTrigger>
                                  <SelectValue placeholder="Select rental period" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value="daily">Daily</SelectItem>
                                <SelectItem value="weekly">Weekly</SelectItem>
                                <SelectItem value="monthly">Monthly</SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="rentalPrice"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Rental Price</FormLabel>
                            <FormControl>
                              <Input type="number" step="0.01" placeholder="0.00" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>
                  )}

                  <FormField
                    control={form.control}
                    name="imageUrl"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Product Image</FormLabel>
                        <div className="space-y-2">
                          <FormControl>
                            <Input placeholder="https://example.com/image.jpg" {...field} value={field.value || ""} />
                          </FormControl>
                          <div className="flex items-center gap-2">
                            <Button
                              type="button"
                              variant="outline"
                              size="sm"
                              onClick={() => document.getElementById('product-image-upload')?.click()}
                              disabled={isUploading}
                              className="flex items-center gap-2"
                            >
                              <Upload className="h-4 w-4" />
                              {isUploading ? "Uploading..." : "Upload Image"}
                            </Button>
                            <input
                              id="product-image-upload"
                              type="file"
                              accept="image/*"
                              className="hidden"
                              onChange={handleImageUpload}
                            />
                          </div>
                          {field.value && (
                            <img
                              src={field.value}
                              alt="Product preview"
                              className="w-24 h-24 object-cover rounded border"
                            />
                          )}
                        </div>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="flex gap-4">
                    <FormField
                      control={form.control}
                      name="isActive"
                      render={({ field }) => (
                        <FormItem className="flex flex-row items-start space-x-3 space-y-0">
                          <FormControl>
                            <Checkbox
                              checked={field.value || false}
                              onCheckedChange={field.onChange}
                            />
                          </FormControl>
                          <FormLabel>Active</FormLabel>
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="isFeatured"
                      render={({ field }) => (
                        <FormItem className="flex flex-row items-start space-x-3 space-y-0">
                          <FormControl>
                            <Checkbox
                              checked={field.value || false}
                              onCheckedChange={field.onChange}
                            />
                          </FormControl>
                          <FormLabel>Featured</FormLabel>
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="flex justify-end gap-2">
                    <Button type="button" variant="outline" onClick={handleCloseDialog}>
                      Cancel
                    </Button>
                    <Button 
                      type="submit" 
                      disabled={createMutation.isPending || updateMutation.isPending}
                    >
                      {editingProduct ? "Update" : "Create"}
                    </Button>
                  </div>
                </form>
              </Form>
            </DialogContent>
        </Dialog>
        <div className="grid gap-4">
          {products?.length === 0 ? (
            <p className="text-center text-muted-foreground py-8">
              No products found. Create your first product to get started.
            </p>
          ) : (
            products?.map((product) => (
              <div
                key={product.id}
                className="flex items-center justify-between p-4 border rounded-lg"
              >
                <div className="flex-1">
                  <div className="flex items-center gap-2">
                    <h3 className="font-semibold">{product.name}</h3>
                    <span className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded">
                      {(product as any).productType || "sale"}
                    </span>
                    {product.isFeatured && (
                      <span className="text-xs bg-yellow-100 text-yellow-800 px-2 py-1 rounded">
                        Featured
                      </span>
                    )}
                    {!product.isActive && (
                      <span className="text-xs bg-red-100 text-red-800 px-2 py-1 rounded">
                        Inactive
                      </span>
                    )}
                  </div>
                  <p className="text-sm text-muted-foreground mt-1">
                    {(product as any).productType === "rental" ? (
                      <>
                        Rental Price: ${(product as any).rentalPrice || "0.00"} / {(product as any).rentalPeriod || "period"} | Regular Price: ${product.price}
                      </>
                    ) : (
                      <>
                        Price: ${product.price}
                      </>
                    )} | SKU: {product.sku}
                  </p>
                  {product.description && (
                    <p className="text-sm text-muted-foreground mt-1 line-clamp-2">
                      {product.description}
                    </p>
                  )}
                </div>
                <div className="flex items-center gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handleEdit(product)}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => deleteMutation.mutate(product.id)}
                    disabled={deleteMutation.isPending}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            ))
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/admin/RolePermissi.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Separator } from "@/components/ui/separator";
import { Users, Shield, Settings, Package, ShoppingCart, Tag, Ruler, Image, BarChart3, Mail } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface Role {
  id: string;
  name: string;
  displayName: string;
  description: string;
  isSystemRole: boolean;
  isActive: boolean;
}

interface PermissionModule {
  id: string;
  name: string;
  displayName: string;
  description: string;
  icon: string;
  sortOrder: number;
}

interface Permission {
  id: string;
  moduleId: string;
  name: string;
  displayName: string;
  description: string;
  action: string;
}

interface User {
  id: string;
  username: string;
  email: string;
  firstName: string;
  lastName: string;
  isAdmin: boolean;
  isSuperAdmin: boolean;
  roleId: string | null;
}

const iconMap = {
  Users,
  Shield,
  Settings,
  Package,
  ShoppingCart,
  Tag,
  Ruler,
  Image,
  BarChart3,
  Mail
};

export default function RolePermissionManager() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedRole, setSelectedRole] = useState<string>("");
  const [selectedUser, setSelectedUser] = useState<string>("");
  const [selectedModule, setSelectedModule] = useState<string>("");
  const [selectedPermissions, setSelectedPermissions] = useState<string[]>([]);

  // Fetch roles
  const { data: roles = [] } = useQuery<Role[]>({
    queryKey: ["/api/admin/roles"],
  });

  // Fetch permission modules
  const { data: modules = [] } = useQuery<PermissionModule[]>({
    queryKey: ["/api/admin/permission-modules"],
  });

  // Fetch users
  const { data: users = [] } = useQuery<User[]>({
    queryKey: ["/api/admin/users"],
  });

  // Fetch permissions for selected module
  const { data: modulePermissions = [] } = useQuery<Permission[]>({
    queryKey: ["/api/admin/permissions", selectedModule],
    enabled: !!selectedModule,
  });

  // Fetch current role permissions
  const { data: rolePermissions = [] } = useQuery<Permission[]>({
    queryKey: ["/api/admin/role-permissions", selectedRole],
    enabled: !!selectedRole,
  });

  // Mutation to assign permissions to role
  const assignPermissionsMutation = useMutation({
    mutationFn: async ({ roleId, permissionIds }: { roleId: string; permissionIds: string[] }) => {
      await apiRequest(`/api/admin/assign-permissions`, "POST", { roleId, permissionIds });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Permissions assigned successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/role-permissions"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to assign permissions",
        variant: "destructive",
      });
    },
  });

  // Mutation to assign role to user
  const assignRoleMutation = useMutation({
    mutationFn: async ({ userId, roleId }: { userId: string; roleId: string }) => {
      await apiRequest(`/api/admin/assign-role`, "POST", { userId, roleId });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Role assigned successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to assign role",
        variant: "destructive",
      });
    },
  });

  // Update selected permissions when role changes
  useEffect(() => {
    if (rolePermissions.length > 0) {
      setSelectedPermissions(rolePermissions.map(p => p.id));
    } else {
      setSelectedPermissions([]);
    }
  }, [rolePermissions]);

  const handlePermissionToggle = (permissionId: string) => {
    setSelectedPermissions(prev => 
      prev.includes(permissionId)
        ? prev.filter(id => id !== permissionId)
        : [...prev, permissionId]
    );
  };

  const handleSavePermissions = () => {
    if (!selectedRole) {
      toast({
        title: "Error",
        description: "Please select a role first",
        variant: "destructive",
      });
      return;
    }

    assignPermissionsMutation.mutate({
      roleId: selectedRole,
      permissionIds: selectedPermissions,
    });
  };

  const handleAssignRole = () => {
    if (!selectedUser || !selectedRole) {
      toast({
        title: "Error",
        description: "Please select both user and role",
        variant: "destructive",
      });
      return;
    }

    assignRoleMutation.mutate({
      userId: selectedUser,
      roleId: selectedRole,
    });
  };

  const getIconComponent = (iconName: string) => {
    const IconComponent = iconMap[iconName as keyof typeof iconMap] || Shield;
    return <IconComponent className="h-5 w-5" />;
  };

  const roleOptions = roles.filter(role => !role.isSystemRole); // Hide Super Admin role from assignment

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-2xl font-bold">Role & Permission Management</h2>
        <p className="text-muted-foreground">
          Manage user roles and assign specific permissions (Super Admin Only)
        </p>
      </div>

      <Tabs defaultValue="permissions" className="space-y-4">
        <TabsList>
          <TabsTrigger value="permissions">Manage Permissions</TabsTrigger>
          <TabsTrigger value="assign-roles">Assign Roles to Users</TabsTrigger>
          <TabsTrigger value="overview">System Overview</TabsTrigger>
        </TabsList>

        <TabsContent value="permissions" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Permission Assignment</CardTitle>
              <CardDescription>
                Select a role and assign specific permissions to control access
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {/* Role Selection */}
              <div className="space-y-2">
                <label className="text-sm font-medium">Select Role</label>
                <Select value={selectedRole} onValueChange={setSelectedRole}>
                  <SelectTrigger>
                    <SelectValue placeholder="Choose a role to manage" />
                  </SelectTrigger>
                  <SelectContent>
                    {roleOptions.map((role) => (
                      <SelectItem key={role.id} value={role.id}>
                        <div className="flex items-center space-x-2">
                          <Shield className="h-4 w-4" />
                          <span>{role.displayName}</span>
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {selectedRole && (
                <div className="space-y-4">
                  <Separator />

                  {/* Module Selection */}
                  <div className="space-y-2">
                    <label className="text-sm font-medium">Permission Modules</label>
                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-2">
                      {modules.map((module) => (
                        <Button
                          key={module.id}
                          variant={selectedModule === module.id ? "default" : "outline"}
                          size="sm"
                          onClick={() => setSelectedModule(module.id)}
                          className="h-auto p-3 flex flex-col items-center space-y-1"
                        >
                          {getIconComponent(module.icon)}
                          <span className="text-xs text-center">{module.displayName}</span>
                        </Button>
                      ))}
                    </div>
                  </div>

                  {/* Permission Selection */}
                  {selectedModule && modulePermissions.length > 0 && (
                    <div className="space-y-3">
                      <h4 className="font-medium">
                        {modules.find(m => m.id === selectedModule)?.displayName} Permissions
                      </h4>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                        {modulePermissions.map((permission) => (
                          <div key={permission.id} className="flex items-center space-x-2 p-3 border rounded-lg">
                            <Checkbox
                              id={permission.id}
                              checked={selectedPermissions.includes(permission.id)}
                              onCheckedChange={() => handlePermissionToggle(permission.id)}
                            />
                            <div className="space-y-1">
                              <label 
                                htmlFor={permission.id} 
                                className="text-sm font-medium cursor-pointer"
                              >
                                {permission.displayName}
                              </label>
                              <Badge variant="secondary" className="text-xs">
                                {permission.action}
                              </Badge>
                              {permission.description && (
                                <p className="text-xs text-muted-foreground">
                                  {permission.description}
                                </p>
                              )}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  <div className="flex justify-end space-x-2">
                    <Button 
                      onClick={handleSavePermissions}
                      disabled={assignPermissionsMutation.isPending}
                    >
                      {assignPermissionsMutation.isPending ? "Saving..." : "Save Permissions"}
                    </Button>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="assign-roles" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Assign Roles to Users</CardTitle>
              <CardDescription>
                Assign roles to users to give them specific permissions
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {/* User Selection */}
                <div className="space-y-2">
                  <label className="text-sm font-medium">Select User</label>
                  <Select value={selectedUser} onValueChange={setSelectedUser}>
                    <SelectTrigger>
                      <SelectValue placeholder="Choose a user" />
                    </SelectTrigger>
                    <SelectContent>
                      {users.filter(user => !user.isSuperAdmin).map((user) => (
                        <SelectItem key={user.id} value={user.id}>
                          <div className="flex items-center space-x-2">
                            <Users className="h-4 w-4" />
                            <span>{user.username} ({user.email})</span>
                            {user.isAdmin && (
                              <Badge variant="secondary" className="text-xs">Admin</Badge>
                            )}
                          </div>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* Role Selection */}
                <div className="space-y-2">
                  <label className="text-sm font-medium">Select Role</label>
                  <Select value={selectedRole} onValueChange={setSelectedRole}>
                    <SelectTrigger>
                      <SelectValue placeholder="Choose a role" />
                    </SelectTrigger>
                    <SelectContent>
                      {roleOptions.map((role) => (
                        <SelectItem key={role.id} value={role.id}>
                          <div className="flex items-center space-x-2">
                            <Shield className="h-4 w-4" />
                            <span>{role.displayName}</span>
                          </div>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="flex justify-end">
                <Button
                  onClick={handleAssignRole}
                  disabled={assignRoleMutation.isPending}
                >
                  {assignRoleMutation.isPending ? "Assigning..." : "Assign Role"}
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Current User-Role Assignments */}
          <Card>
            <CardHeader>
              <CardTitle>Current Role Assignments</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {users.map((user) => (
                  <div key={user.id} className="flex items-center justify-between p-3 border rounded-lg">
                    <div className="flex items-center space-x-3">
                      <Users className="h-4 w-4 text-muted-foreground" />
                      <div>
                        <p className="font-medium">{user.username}</p>
                        <p className="text-sm text-muted-foreground">{user.email}</p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      {user.isSuperAdmin ? (
                        <Badge variant="destructive">Super Admin</Badge>
                      ) : user.isAdmin ? (
                        <Badge variant="default">Admin</Badge>
                      ) : (
                        <Badge variant="secondary">User</Badge>
                      )}
                      {user.roleId && (
                        <Badge variant="outline">
                          {roles.find(r => r.id === user.roleId)?.displayName || "Unknown Role"}
                        </Badge>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="overview" className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <Card>
              <CardHeader className="pb-2">
                <CardTitle className="text-lg">System Roles</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{roles.length}</div>
                <p className="text-sm text-muted-foreground">Total roles in system</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="pb-2">
                <CardTitle className="text-lg">Permission Modules</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{modules.length}</div>
                <p className="text-sm text-muted-foreground">Available modules</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="pb-2">
                <CardTitle className="text-lg">Total Permissions</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">46</div>
                <p className="text-sm text-muted-foreground">System permissions</p>
              </CardContent>
            </Card>
          </div>

          <Card>
            <CardHeader>
              <CardTitle>Permission Modules Overview</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {modules.map((module) => (
                  <div key={module.id} className="p-4 border rounded-lg">
                    <div className="flex items-center space-x-3 mb-2">
                      {getIconComponent(module.icon)}
                      <h4 className="font-medium">{module.displayName}</h4>
                    </div>
                    <p className="text-sm text-muted-foreground">{module.description}</p>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/components/admin/SiteSettings.content:
import React, { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertSiteSettingsSchema, type InsertSiteSettings, type SiteSettings } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Settings, Palette, Mail, Upload, Save, Monitor, Loader2, CheckCircle, ShoppingCart, Bell } from "lucide-react";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { themes, applyTheme, type ThemeName } from "@/lib/themes";

export function SiteSettings() {
  const { toast } = useToast();
  const [isUploading, setIsUploading] = useState(false);
  const [isTestingEmail, setIsTestingEmail] = useState(false);
  const [testEmailAddress, setTestEmailAddress] = useState("");
  const [smtpStatus, setSmtpStatus] = useState<any>(null);



  const { data: settings, isLoading } = useQuery<SiteSettings>({
    queryKey: ["/api/settings"],
  });

  const form = useForm<InsertSiteSettings>({
    resolver: zodResolver(insertSiteSettingsSchema),
    defaultValues: {
      siteName: settings?.siteName || "BAYG",
      logoUrl: settings?.logoUrl || "",
      contactEmail: settings?.contactEmail || "",
      supportEmail: settings?.supportEmail || "",
      adminEmail: settings?.adminEmail || "",
      contactPhone: settings?.contactPhone || "",
      contactAddress: settings?.contactAddress || "",
      businessHours: settings?.businessHours || "",
      officeHoursTitle: settings?.officeHoursTitle || "Office Hours",
      paymentMethodsImage: settings?.paymentMethodsImage || "",
      footerLeftImage: settings?.footerLeftImage || "",
      footerLeftImageWidth: settings?.footerLeftImageWidth || 200,
      paymentMethodsImageWidth: settings?.paymentMethodsImageWidth || 250,
      loginPageLogoWidth: settings?.loginPageLogoWidth || 80,
      headerLogoHeight: settings?.headerLogoHeight || 64,
      theme: settings?.theme || "default",
      textColor: settings?.textColor || "#1e293b",
      headerTextColor: settings?.headerTextColor || "#64748b",
      tabTextColor: settings?.tabTextColor || "#2563eb", 
      tabActiveTextColor: settings?.tabActiveTextColor || "#2563eb",
      orderConfirmationTemplate: settings?.orderConfirmationTemplate || "",
      footerDescription: settings?.footerDescription || "",
      footerBackgroundUrl: settings?.footerBackgroundUrl || "/uploads/footer-background.png",
      quickLinksTitle: settings?.quickLinksTitle || "Quick Links",
      quickLinkHome: settings?.quickLinkHome || "Home",
      quickLinkProducts: settings?.quickLinkProducts || "Products",
      quickLinkAbout: settings?.quickLinkAbout || "About",
      quickLinkContact: settings?.quickLinkContact || "Contact",
      socialTitle: settings?.socialTitle || "Follow Us",
      socialFacebook: settings?.socialFacebook || "",
      socialTwitter: settings?.socialTwitter || "",
      socialInstagram: settings?.socialInstagram || "",
      socialLinkedin: settings?.socialLinkedin || "",
      copyrightText: settings?.copyrightText || "",
      additionalFooterText: settings?.additionalFooterText || "",
      // SMTP Email Configuration
      smtpEnabled: settings?.smtpEnabled || false,
      smtpHost: settings?.smtpHost || "",
      smtpPort: settings?.smtpPort || 587,
      smtpSecure: settings?.smtpSecure || false,
      smtpUser: settings?.smtpUser || "",
      smtpPassword: settings?.smtpPassword || "",
      smtpFromName: settings?.smtpFromName || "BAYG - Bahrain Asian Youth Games 2025",
      smtpFromEmail: settings?.smtpFromEmail || "",
      // Login Page Settings
      loginPageLogo: settings?.loginPageLogo || "",
      loginPageTitle: settings?.loginPageTitle || "BAYG Store",
    },
  });

  // Update form when settings load - use useEffect to avoid infinite renders
  React.useEffect(() => {
    if (settings) {
      form.reset({
        siteName: settings.siteName,
        logoUrl: settings.logoUrl || "",
        contactEmail: settings.contactEmail || "",
        supportEmail: settings.supportEmail || "",
        adminEmail: settings.adminEmail || "",
        contactPhone: settings.contactPhone || "",
        contactAddress: settings.contactAddress || "",
        businessHours: settings.businessHours || "",
        officeHoursTitle: settings.officeHoursTitle || "Office Hours",
        paymentMethodsImage: settings.paymentMethodsImage || "",
        footerLeftImage: settings.footerLeftImage || "",
        footerLeftImageWidth: settings.footerLeftImageWidth || 200,
        paymentMethodsImageWidth: settings.paymentMethodsImageWidth || 250,
        theme: settings.theme || "default",
        textColor: settings.textColor || "#1e293b",
        headerTextColor: settings.headerTextColor || "#64748b",
        tabTextColor: settings.tabTextColor || "#2563eb",
        tabActiveTextColor: settings.tabActiveTextColor || "#2563eb",
        orderConfirmationTemplate: settings.orderConfirmationTemplate || "",
        footerDescription: settings.footerDescription || "",
        footerBackgroundUrl: settings.footerBackgroundUrl || "/uploads/footer-background.png",
        quickLinksTitle: settings.quickLinksTitle || "Quick Links",
        quickLinkHome: settings.quickLinkHome || "Home",
        quickLinkProducts: settings.quickLinkProducts || "Products",
        quickLinkAbout: settings.quickLinkAbout || "About",
        quickLinkContact: settings.quickLinkContact || "Contact",
        socialTitle: settings.socialTitle || "Follow Us",
        socialFacebook: settings.socialFacebook || "",
        socialTwitter: settings.socialTwitter || "",
        socialInstagram: settings.socialInstagram || "",
        socialLinkedin: settings.socialLinkedin || "",
        copyrightText: settings.copyrightText || "",
        additionalFooterText: settings.additionalFooterText || "",
        // Email configuration - Microsoft 365 hardcoded, only fromName configurable
        smtpFromName: settings.smtpFromName || "BAYG - Bahrain Asian Youth Games 2025",
        emailEnabled: settings.emailEnabled || false,
        // Login Page Settings
        loginPageLogo: settings.loginPageLogo || "",
        loginPageTitle: settings.loginPageTitle || "InnovanceOrbit Store",
        loginPageLogoWidth: settings.loginPageLogoWidth || 80,
        headerLogoHeight: settings.headerLogoHeight || 64,
      });

      // Apply the current theme on load
      if (settings.theme) {
        applyTheme(settings.theme as ThemeName, {
          headerTextColor: settings.headerTextColor || undefined,
          tabTextColor: settings.tabTextColor || undefined,
          tabActiveTextColor: settings.tabActiveTextColor || undefined,
        });
      }
    }
  }, [settings, form]);

  const updateMutation = useMutation({
    mutationFn: async (data: InsertSiteSettings) => {
      const response = await apiRequest("/api/settings", "PUT", data);
      return response.json();
    },
    onSuccess: (result, variables) => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });

      // Apply the theme immediately after successful save
      if (variables.theme) {
        applyTheme(variables.theme as ThemeName, {
          headerTextColor: variables.headerTextColor || undefined,
          tabTextColor: variables.tabTextColor || undefined,
          tabActiveTextColor: variables.tabActiveTextColor || undefined,
        });
      }

      toast({
        title: "Success",
        description: "Site settings and theme updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update settings",
        variant: "destructive",
      });
    },
  });

  const handleLogoUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploading(true);
    try {
      const formData = new FormData();
      formData.append('image', file);

      const response = await fetch('/api/upload-image', {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const data = await response.json();
      form.setValue("logoUrl", data.imageUrl);

      toast({
