        testEmail: testEmailAddress,
      });
      const result = await response.json();

      if (result.success) {
        toast({
          title: "Test Email Sent!",
          description: result.message,
        });
      } else {
        toast({
          title: "Test Email Failed",
          description: result.message,
          variant: "destructive",
        });
      }
    } catch (error: any) {
      toast({
