                      name="paymentMethodsImageWidth"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Payment Methods Image Width (px)</FormLabel>
                          <FormControl>
                            <Input 
                              type="number" 
                              placeholder="250" 
                              {...field} 
                              value={field.value || 250}
                              onChange={(e) => field.onChange(parseInt(e.target.value) || 250)}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>
                </div>
              </TabsContent>

              <TabsContent value="login" className="space-y-4">
                <FormField
                  control={form.control}
                  name="loginPageTitle"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Login Page Title</FormLabel>
                      <FormControl>
                        <Input placeholder="InnovanceOrbit Store" {...field} value={field.value || ""} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="loginPageLogo"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Login Page Logo URL</FormLabel>
                      <FormControl>
                        <Input placeholder="https://example.com/login-logo.png" {...field} value={field.value || ""} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <h3 className="text-sm font-medium">Upload Login Page Logo</h3>
                    <div className="flex items-center space-x-2">
                      <input
                        id="login-page-logo-upload"
                        type="file"
                        accept="image/*"
                        onChange={handleLoginPageLogoUpload}
                        className="hidden"
                      />
                      <Button
                        type="button"
                        variant="outline"
                        onClick={() => document.getElementById("login-page-logo-upload")?.click()}
                        disabled={isUploading}
                      >
                        <Upload className="h-4 w-4 mr-2" />
                        {isUploading ? "Uploading..." : "Upload Logo"}
                      </Button>
                    </div>
                  </div>
                  {form.watch("loginPageLogo") && (
                    <div className="p-4 border rounded-lg bg-gray-50 dark:bg-gray-900">
                      <div className="flex items-center space-x-4">
                        <img 
                          src={form.watch("loginPageLogo") || ""} 
                          alt="Login page logo preview" 
                          className="w-16 h-16 object-contain border rounded"
                        />
                        <div className="flex-1">
                          <p className="text-sm font-medium">Current Login Page Logo</p>
                          <p className="text-xs text-gray-500 break-all">{form.watch("loginPageLogo")}</p>
                        </div>
                      </div>
                    </div>
                  )}
                </div>

                <FormField
                  control={form.control}
                  name="loginPageLogoWidth"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Login Page Logo Width (px)</FormLabel>
                      <FormControl>
                        <Input 
                          type="number" 
                          placeholder="80" 
                          {...field} 
                          value={field.value || 80}
                          onChange={(e) => field.onChange(parseInt(e.target.value) || 80)}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />


              </TabsContent>

              <TabsContent value="email" className="space-y-6">
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="text-lg font-medium">SMTP Configuration</h3>
                      <p className="text-sm text-muted-foreground">
                        Configure email settings for order notifications and confirmations
                      </p>
                    </div>
                    {smtpStatus && (
                      <div className={`px-3 py-1 rounded-full text-xs font-medium ${
                        smtpStatus.configured && smtpStatus.enabled 
                          ? 'bg-green-100 text-green-800' 
                          : 'bg-yellow-100 text-yellow-800'
                      }`}>
                        {smtpStatus.configured && smtpStatus.enabled ? 'Configured' : 'Not Configured'}
                      </div>
                    )}
                  </div>

                  <FormField
                    control={form.control}
                    name="smtpEnabled"
                    render={({ field }) => (
                      <FormItem className="flex flex-row items-center justify-between rounded-lg border p-4">
                        <div className="space-y-0.5">
                          <FormLabel className="text-base">Enable Email Notifications</FormLabel>
                          <p className="text-sm text-muted-foreground">
                            Send automated emails for order confirmations and updates
                          </p>
                        </div>
                        <FormControl>
                          <Switch
                            checked={field.value}
                            onCheckedChange={field.onChange}
                          />
                        </FormControl>
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="smtpHost"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>SMTP Host</FormLabel>
                          <FormControl>
                            <Input placeholder="smtp.gmail.com" {...field} />
                          </FormControl>
                          <p className="text-xs text-muted-foreground">
                            Your email provider's SMTP server
                          </p>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="smtpPort"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>SMTP Port</FormLabel>
                          <FormControl>
                            <Input 
                              type="number" 
                              placeholder="587" 
                              {...field}
                              value={field.value || 587}
                              onChange={(e) => field.onChange(parseInt(e.target.value) || 587)}
                            />
                          </FormControl>
                          <p className="text-xs text-muted-foreground">
                            Usually 587 for TLS or 465 for SSL
                          </p>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="smtpSecure"
                    render={({ field }) => (
                      <FormItem className="flex flex-row items-center justify-between rounded-lg border p-4">
                        <div className="space-y-0.5">
                          <FormLabel className="text-base">Use SSL/TLS</FormLabel>
                          <p className="text-sm text-muted-foreground">
                            Turn OFF for port 587 (most providers). Turn ON only for port 465.
                          </p>
                        </div>
                        <FormControl>
                          <Switch
                            checked={field.value}
                            onCheckedChange={field.onChange}
                          />
                        </FormControl>
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="smtpUser"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>SMTP Username</FormLabel>
                          <FormControl>
                            <Input placeholder="your-email@gmail.com" {...field} />
                          </FormControl>
                          <p className="text-xs text-muted-foreground">
                            Your email address or username
                          </p>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="smtpPassword"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>SMTP Password</FormLabel>
                          <FormControl>
                            <Input 
                              type="password" 
                              placeholder="App password or email password" 
                              {...field} 
                            />
                          </FormControl>
                          <p className="text-xs text-muted-foreground">
                            Use app password for Gmail/Outlook
                          </p>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="smtpFromName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>From Name</FormLabel>
                          <FormControl>
                            <Input placeholder="BAYG - Bahrain Asian Youth Games 2025" {...field} />
                          </FormControl>
                          <p className="text-xs text-muted-foreground">
                            Display name for outgoing emails
                          </p>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="smtpFromEmail"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>From Email</FormLabel>
                          <FormControl>
                            <Input placeholder="noreply@yourdomain.com" {...field} />
                          </FormControl>
                          <p className="text-xs text-muted-foreground">
                            Email address for outgoing emails
                          </p>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="border-t pt-4">
                    <h4 className="text-md font-medium mb-4">Email Testing</h4>
                    <div className="flex items-end gap-4">
                      <div className="flex-1">
                        <Label htmlFor="test-email">Test Email Address</Label>
                        <Input
                          id="test-email"
                          type="email"
                          placeholder="Enter email to test"
                          value={testEmailAddress}
                          onChange={(e) => setTestEmailAddress(e.target.value)}
                        />
                      </div>
                      <Button
                        type="button"
                        variant="outline"
                        onClick={handleTestEmail}
                        disabled={isTestingEmail || !testEmailAddress}
                        className="flex items-center gap-2"
                      >
                        <Mail className="h-4 w-4" />
                        {isTestingEmail ? "Sending..." : "Send Test Email"}
                      </Button>
                    </div>
                    <p className="text-xs text-muted-foreground mt-2">
                      Save your SMTP settings first, then test with a real email address
                    </p>
                  </div>

                  <div className="bg-blue-50 p-4 rounded-lg border-l-4 border-blue-500">
                    <h4 className="font-medium text-blue-900 mb-2">Email Setup Instructions</h4>
                    <div className="text-sm text-blue-800 space-y-2">
                      <div>
                        <p><strong>Gmail:</strong> smtp.gmail.com, port 587, SSL/TLS off</p>
                        <p className="text-xs">Use an App Password (not your regular password)</p>
                      </div>
                      <div>
                        <p><strong>Outlook/Hotmail:</strong> smtp-mail.outlook.com, port 587, SSL/TLS off</p>
                        <p className="text-xs">Regular password or App Password if 2FA is enabled</p>
                      </div>
                      <div>
                        <p><strong>Yahoo:</strong> smtp.mail.yahoo.com, port 587, SSL/TLS off</p>
                        <p className="text-xs">Must use an App Password (Yahoo requires it)</p>
                      </div>
                      <div className="border-t pt-2 mt-2">
                        <p className="font-medium">Common Issues:</p>
                        <ul className="text-xs list-disc list-inside space-y-1">
                          <li>Use port 587 with SSL/TLS disabled for most providers</li>
                          <li>Enable "Less secure app access" or use App Passwords</li>
                          <li>For Gmail: Generate App Password in Google Account settings</li>
                          <li>For Outlook: Use your Microsoft account password or App Password</li>
                        </ul>
                      </div>
                    </div>
                  </div>
                </div>
              </TabsContent>

              <div className="flex justify-end pt-6 border-t">
                <Button 
                  type="submit" 
                  disabled={updateMutation.isPending}
                  className="flex items-center gap-2"
                >
                  <Save className="h-4 w-4" />
                  {updateMutation.isPending ? "Saving..." : "Save Settings"}
                </Button>
              </div>
            </Tabs>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

client/src/components/admin/SliderManage.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Skeleton } from "@/components/ui/skeleton";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertSliderImageSchema } from "@shared/schema";
import { Plus, Upload, Edit, Trash2, Eye, EyeOff, GripVertical } from "lucide-react";
import type { SliderImage, InsertSliderImage } from "@shared/schema";

export function SliderManager() {
  const { toast } = useToast();
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [editingSlider, setEditingSlider] = useState<SliderImage | null>(null);
  const [uploadingImage, setUploadingImage] = useState(false);

  const { data: sliderImages, isLoading } = useQuery({
    queryKey: ["/api/slider-images"],
  });

  const form = useForm<InsertSliderImage>({
    resolver: zodResolver(insertSliderImageSchema),
    defaultValues: {
      title: "",
      description: "",
      imageUrl: "",
      isActive: true,
      sortOrder: 0,
    },
  });

  const createSliderMutation = useMutation({
    mutationFn: async (data: InsertSliderImage) => {
      const res = await apiRequest("/api/slider-images", "POST", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/slider-images"] });
      setIsAddDialogOpen(false);
      form.reset();
      toast({
        title: "Success",
        description: "Slider image added successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add slider image",
        variant: "destructive",
      });
    },
  });

  const updateSliderMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<SliderImage> }) => {
      const res = await apiRequest(`/api/slider-images/${id}`, "PATCH", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/slider-images"] });
      setEditingSlider(null);
      toast({
        title: "Success",
        description: "Slider image updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update slider image",
        variant: "destructive",
      });
    },
  });

  const deleteSliderMutation = useMutation({
    mutationFn: async (id: string) => {
      const res = await apiRequest(`/api/slider-images/${id}`, "DELETE");
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/slider-images"] });
      toast({
        title: "Success",
        description: "Slider image deleted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete slider image",
        variant: "destructive",
      });
    },
  });

  const handleImageUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setUploadingImage(true);
    const formData = new FormData();
    formData.append('image', file);

    try {
      const response = await fetch('/api/upload-image', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        form.setValue('imageUrl', data.imageUrl);
        toast({
          title: "Success",
          description: "Image uploaded successfully",
        });
      } else {
        throw new Error('Upload failed');
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to upload image",
        variant: "destructive",
      });
    } finally {
      setUploadingImage(false);
    }
  };

  const onSubmit = (data: InsertSliderImage) => {
    if (editingSlider) {
      updateSliderMutation.mutate({ id: editingSlider.id, data });
    } else {
      createSliderMutation.mutate(data);
    }
  };

  const toggleSliderStatus = (slider: SliderImage) => {
    updateSliderMutation.mutate({
      id: slider.id,
      data: { isActive: !slider.isActive }
    });
  };

  const handleEdit = (slider: SliderImage) => {
    setEditingSlider(slider);
    form.reset({
      title: slider.title || "",
      description: slider.description || "",
      imageUrl: slider.imageUrl,
      isActive: slider.isActive,
      sortOrder: slider.sortOrder,
    });
    setIsAddDialogOpen(true);
  };

  const handleClose = () => {
    setIsAddDialogOpen(false);
    setEditingSlider(null);
    form.reset();
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Slider Images</h2>
          <p className="text-gray-600">Manage homepage slider images</p>
        </div>
        <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={() => setEditingSlider(null)}>
              <Plus className="h-4 w-4 mr-2" />
              Add Slider Image
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>{editingSlider ? "Edit" : "Add"} Slider Image</DialogTitle>
              <DialogDescription>
                {editingSlider ? "Update" : "Create a new"} slider image for the homepage.
              </DialogDescription>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="title"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Title (Optional)</FormLabel>
                      <FormControl>
                        <Input placeholder="Enter slide title" {...field} value={field.value || ""} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description (Optional)</FormLabel>
                      <FormControl>
                        <Textarea placeholder="Enter slide description" {...field} value={field.value || ""} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="imageUrl"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Image *</FormLabel>
                      <FormControl>
                        <div className="space-y-2">
                          <Input placeholder="Image URL" {...field} />
                          <div className="flex items-center gap-2">
                            <Input
                              type="file"
                              accept="image/*"
                              onChange={handleImageUpload}
                              className="hidden"
                              id="slider-image-upload"
                            />
                            <Label htmlFor="slider-image-upload" className="cursor-pointer">
                              <Button
                                type="button"
                                variant="outline"
                                size="sm"
                                disabled={uploadingImage}
                                asChild
                              >
                                <span>
                                  <Upload className="h-4 w-4 mr-2" />
                                  {uploadingImage ? "Uploading..." : "Upload Image"}
                                </span>
                              </Button>
                            </Label>
                          </div>
                          {field.value && (
                            <img
                              src={field.value}
                              alt="Preview"
                              className="w-full h-32 object-cover rounded border"
                            />
                          )}
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="sortOrder"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Sort Order</FormLabel>
                      <FormControl>
                        <Input
                          type="number"
                          placeholder="0"
                          {...field}
                          value={field.value || 0}
                          onChange={(e) => field.onChange(parseInt(e.target.value) || 0)}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="isActive"
                  render={({ field }) => (
                    <FormItem className="flex items-center justify-between">
                      <FormLabel>Active</FormLabel>
                      <FormControl>
                        <Switch
                          checked={field.value || false}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex gap-2 pt-4">
                  <Button
                    type="submit"
                    disabled={createSliderMutation.isPending || updateSliderMutation.isPending}
                    className="flex-1"
                  >
                    {createSliderMutation.isPending || updateSliderMutation.isPending
                      ? "Saving..."
                      : editingSlider
                      ? "Update"
                      : "Add"}
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={handleClose}
                    className="flex-1"
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Slider Images List */}
      <div className="space-y-4">
        {isLoading ? (
          Array.from({ length: 3 }).map((_, i) => (
            <Card key={i}>
              <CardContent className="p-4">
                <div className="flex items-center space-x-4">
                  <Skeleton className="h-20 w-32" />
                  <div className="flex-1 space-y-2">
                    <Skeleton className="h-4 w-32" />
                    <Skeleton className="h-3 w-48" />
                  </div>
                  <div className="flex space-x-2">
                    <Skeleton className="h-8 w-8" />
                    <Skeleton className="h-8 w-8" />
                    <Skeleton className="h-8 w-8" />
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        ) : Array.isArray(sliderImages) && sliderImages.length === 0 ? (
          <Card>
            <CardContent className="text-center py-8">
              <p className="text-gray-500">No slider images found. Add your first slider image to get started.</p>
            </CardContent>
          </Card>
        ) : (
          Array.isArray(sliderImages) ? sliderImages.map((slider: SliderImage) => (
            <Card key={slider.id} className="hover:shadow-md transition-shadow">
              <CardContent className="p-4">
                <div className="flex items-center space-x-4">
                  <div className="cursor-move">
                    <GripVertical className="h-5 w-5 text-gray-400" />
                  </div>
                  <img
                    src={slider.imageUrl}
                    alt={slider.title || "Slider image"}
                    className="h-20 w-32 object-cover rounded border"
                  />
                  <div className="flex-1">
                    <h3 className="font-medium text-gray-900">
                      {slider.title || "Untitled"}
                    </h3>
                    <p className="text-sm text-gray-500 mt-1">
                      {slider.description || "No description"}
                    </p>
                    <div className="flex items-center gap-2 mt-2">
                      <span className="text-xs text-gray-400">Order: {slider.sortOrder}</span>
                      <span className={`text-xs px-2 py-1 rounded ${
                        slider.isActive 
                          ? "bg-green-100 text-green-800" 
                          : "bg-gray-100 text-gray-800"
                      }`}>
                        {slider.isActive ? "Active" : "Inactive"}
                      </span>
                    </div>
                  </div>
                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => toggleSliderStatus(slider)}
                      disabled={updateSliderMutation.isPending}
                    >
                      {slider.isActive ? (
                        <EyeOff className="h-4 w-4" />
                      ) : (
                        <Eye className="h-4 w-4" />
                      )}
                    </Button>
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => handleEdit(slider)}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => deleteSliderMutation.mutate(slider.id)}
                      disabled={deleteSliderMutation.isPending}
                    >
                      <Trash2 className="h-4 w-4 text-red-500" />
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          )) : null
        )}
      </div>
    </div>
  );
}

client/src/components/admin/UnitsOfMeasu.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Checkbox } from "@/components/ui/checkbox";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertUnitOfMeasureSchema, type InsertUnitOfMeasure, type UnitOfMeasure } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Plus, Edit, Trash2, Ruler } from "lucide-react";
import { z } from "zod";

const enhancedUnitSchema = insertUnitOfMeasureSchema.extend({
  isActive: z.boolean().default(true),
});

type EnhancedInsertUnit = z.infer<typeof enhancedUnitSchema>;

export function UnitsOfMeasureManager() {
  const { toast } = useToast();
  const [isAddOpen, setIsAddOpen] = useState(false);
  const [editingUnit, setEditingUnit] = useState<UnitOfMeasure | null>(null);

  const { data: units, isLoading: unitsLoading } = useQuery<UnitOfMeasure[]>({
    queryKey: ["/api/admin/units-of-measure"],
  });

  const form = useForm<EnhancedInsertUnit>({
    resolver: zodResolver(enhancedUnitSchema),
    defaultValues: {
      name: "",
      abbreviation: "",
      isActive: true,
    },
  });

  const createUnitMutation = useMutation({
    mutationFn: async (unitData: InsertUnitOfMeasure) => {
      const res = await apiRequest("/api/admin/units-of-measure", "POST", unitData);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/units-of-measure"] });
      queryClient.invalidateQueries({ queryKey: ["/api/units-of-measure"] });
      setIsAddOpen(false);
      setEditingUnit(null);
      form.reset();
      toast({
        title: "Success",
        description: "Unit of measure created successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create unit of measure",
        variant: "destructive",
      });
    },
  });

  const updateUnitMutation = useMutation({
    mutationFn: async ({ id, unitData }: { id: string; unitData: Partial<InsertUnitOfMeasure> }) => {
      const res = await apiRequest(`/api/admin/units-of-measure/${id}`, "PUT", unitData);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/units-of-measure"] });
      queryClient.invalidateQueries({ queryKey: ["/api/units-of-measure"] });
      setEditingUnit(null);
      form.reset();
      toast({
        title: "Success",
        description: "Unit of measure updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update unit of measure",
        variant: "destructive",
      });
    },
  });

  const deleteUnitMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest(`/api/admin/units-of-measure/${id}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/units-of-measure"] });
      queryClient.invalidateQueries({ queryKey: ["/api/units-of-measure"] });
      toast({
        title: "Success",
        description: "Unit of measure deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete unit of measure",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (data: EnhancedInsertUnit) => {
    if (editingUnit) {
      updateUnitMutation.mutate({ id: editingUnit.id, unitData: data });
    } else {
      createUnitMutation.mutate(data);
    }
  };

  const startEdit = (unit: UnitOfMeasure) => {
    setEditingUnit(unit);
    form.reset({
      name: unit.name,
      abbreviation: unit.abbreviation,
      isActive: unit.isActive ?? true,
    });
  };

  const handleDelete = (id: string) => {
    if (confirm("Are you sure you want to delete this unit of measure?")) {
      deleteUnitMutation.mutate(id);
    }
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="flex items-center gap-2">
              <Ruler className="w-5 h-5" />
              Units of Measure
            </CardTitle>
            <CardDescription>
              Manage units of measure for products (kg, pieces, liters, etc.)
            </CardDescription>
          </div>
          <Dialog open={isAddOpen || !!editingUnit} onOpenChange={(open) => {
            if (!open) {
              setIsAddOpen(false);
              setEditingUnit(null);
              form.reset();
            }
          }}>
            <DialogTrigger asChild>
              <Button onClick={() => setIsAddOpen(true)}>
                <Plus className="w-4 h-4 mr-2" />
                Add Unit
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>
                  {editingUnit ? "Edit Unit of Measure" : "Add New Unit of Measure"}
                </DialogTitle>
                <DialogDescription>
                  {editingUnit ? "Update the unit of measure details" : "Create a new unit of measure for products"}
                </DialogDescription>
              </DialogHeader>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Name</FormLabel>
                        <FormControl>
                          <Input placeholder="e.g., kilogram, piece, liter" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="abbreviation"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Abbreviation</FormLabel>
                        <FormControl>
                          <Input placeholder="e.g., kg, pc, L" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="isActive"
                    render={({ field }) => (
                      <FormItem className="flex flex-row items-start space-x-3 space-y-0">
                        <FormControl>
                          <Checkbox 
                            checked={field.value} 
                            onCheckedChange={field.onChange} 
                          />
                        </FormControl>
                        <div className="space-y-1 leading-none">
                          <FormLabel>Active</FormLabel>
                          <p className="text-sm text-muted-foreground">
                            Active units are available for selection in product forms
                          </p>
                        </div>
                      </FormItem>
                    )}
                  />
                  <div className="flex justify-end gap-2">
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => {
                        setIsAddOpen(false);
                        setEditingUnit(null);
                        form.reset();
                      }}
                    >
                      Cancel
                    </Button>
                    <Button 
                      type="submit" 
                      disabled={createUnitMutation.isPending || updateUnitMutation.isPending}
                    >
                      {createUnitMutation.isPending || updateUnitMutation.isPending
                        ? "Saving..."
                        : editingUnit
                        ? "Update Unit"
                        : "Create Unit"
                      }
                    </Button>
                  </div>
                </form>
              </Form>
            </DialogContent>
          </Dialog>
        </div>
      </CardHeader>
      <CardContent>
        {unitsLoading ? (
          <div className="space-y-2">
            {[...Array(5)].map((_, i) => (
              <div key={i} className="h-12 bg-gray-100 rounded animate-pulse" />
            ))}
          </div>
        ) : units && units.length > 0 ? (
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Name</TableHead>
                <TableHead>Abbreviation</TableHead>
                <TableHead>Status</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {units.map((unit) => (
                <TableRow key={unit.id}>
                  <TableCell className="font-medium">{unit.name}</TableCell>
                  <TableCell>{unit.abbreviation}</TableCell>
                  <TableCell>
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                      unit.isActive 
                        ? "bg-green-100 text-green-800" 
                        : "bg-gray-100 text-gray-800"
                    }`}>
                      {unit.isActive ? "Active" : "Inactive"}
                    </span>
                  </TableCell>
                  <TableCell className="text-right">
                    <div className="flex items-center gap-2 justify-end">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => startEdit(unit)}
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleDelete(unit.id)}
                        disabled={deleteUnitMutation.isPending}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        ) : (
          <div className="text-center py-8 text-gray-500">
            No units of measure found. Create your first unit to get started.
          </div>
        )}
      </CardContent>
    </Card>
  );
}

